export default function App() {
  return (
    <>
      <Panel className="w-80 rounded-xl border">
        <Panel.Title title="Select Time" />
        <Panel.Row>
          <Select
            value={format}
            onChange={(value) => setFormat(value as Format)}
          >
            <Select.Trigger className="[grid-area:input]">
              <Select.Value>
                {format === Format.HH_MM
                  ? "24-hour format (HH:mm)"
                  : "12-hour format (h:mm a)"}
              </Select.Value>
            </Select.Trigger>
            <Select.Content>
              <Select.Item value={Format.HH_MM}>
                24-hour format (HH:mm)
              </Select.Item>
              <Select.Item value={Format.H_MM_A}>
                12-hour format (h:mm a)
              </Select.Item>
            </Select.Content>
          </Select>
        </Panel.Row>
        <Panel.Row type="single" ref={timeRef} className="time-input">
          <TimeInput
            format={format}
            className="[grid-area:input]"
            value={value}
            onChange={setValue}
            onFocus={() => {
              setActiveInput("single");
              setOpen(true);
            }}
            step={15}
            onEnterKeyDown={() => setOpen(false)}
          />
        </Panel.Row>

        <Panel.Row
          className="time-range-input"
          type="two-input-two-icon"
          ref={rangeRef}
        >
          <TimeRangeInput
            format={format}
            startValue={startTime}
            endValue={endTime}
            onStartChange={(newStart) => {
              if (newStart) {
                // ËÆ°ÁÆóÂΩìÂâçrangeÈïøÂ∫¶ÔºàÊØ´ÁßíÔºâÔºåfallback‰∏∫1Â∞èÊó∂
                const currentRange =
                  startTime && endTime
                    ? endTime.getTime() - startTime.getTime()
                    : 1 * 60 * 60 * 1000;
                // ‰øùÊåÅrangeÈïøÂ∫¶
                const newEnd = new Date(newStart.getTime() + currentRange);
                setStartTime(newStart);
                setEndTime(newEnd);
              } else {
                setStartTime(newStart);
              }
            }}
            onEndChange={(newEnd) => {
              if (newEnd && startTime && newEnd <= startTime) {
                // end <= start Êó∂Êé®Âä®start
                setStartTime(newEnd);
              }
              setEndTime(newEnd);
            }}
            onStartFocus={() => {
              setActiveInput("range-start");
              setOpen(true);
            }}
            onEndFocus={() => {
              setActiveInput("range-end");
              setOpen(true);
            }}
            onEnterKeyDown={() => setOpen(false)}
          />
        </Panel.Row>
      </Panel>
      <Popover
        interactions="focus"
        open={open}
        onOpenChange={setOpen}
        outsidePressIgnore={
          activeInput === "single" ? "time-input" : "time-range-input"
        }
        triggerRef={currentTriggerRef}
        placement="left-start"
        focusManagerProps={{
          initialFocus: -1,
          returnFocus: false,
        }}
      >
        <Popover.Content className="overflow-hidden">
          <TimeCalendar
            format={format}
            className="h-64"
            value={currentValue}
            onChange={handleValueChange}
          />
        </Popover.Content>
      </Popover>
    </>
  );
}

