import { AlertDialog } from "@choice-ui/alert-dialog"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <div className="grid grid-cols-2 gap-4">
        <Button
          onClick={showDefaultConfirm}
          variant="secondary"
        >
          Default Variant
        </Button>
        <Button
          onClick={showDangerConfirm}
          variant="destructive"
        >
          Danger Variant
        </Button>
        <Button
          onClick={showSuccessAlert}
          variant="success"
        >
          Success Variant
        </Button>
        <Button
          onClick={showWarningConfirm}
          variant="secondary"
        >
          Warning Variant
        </Button>
      </div>

      {Object.keys(results).length > 0 && (
        <div className="text-secondary-foreground text-body-small">
          <p>Results:</p>
          <ul className="list-inside list-disc">
            {Object.entries(results).map(([variant, result]) => (
              <li key={variant}>
                {variant}:{" "}
                {typeof result === "boolean"
                  ? result
                    ? "Confirmed"
                    : "Cancelled"
                  : "Acknowledged"}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

