import { Badge } from "@choice-ui/badge"

export default function App() {
  return (
    <div className="flex flex-col items-start gap-4">
      <div className="grid grid-cols-4 place-items-start gap-4 capitalize">
        {Object.values(Variant).map((variant) => (
          <Fragment key={variant}>
            {Object.values(Strong).map((strong) => (
              <Fragment key={strong}>
                <Badge
                  variant={variant}
                  strong={strong === Strong.True}
                >
                  {variant}
                </Badge>
                <Badge
                  variant={variant}
                  strong={strong === Strong.True}
                >
                  <Dot />
                  {variant}
                </Badge>
              </Fragment>
            ))}
          </Fragment>
        ))}
      </div>
    </div>
  );
}

import { Bell, bells } from "@choice-ui/bells"

export default function App() {
  return (
    <div className="flex gap-2 capitalize">
      <Toaster />

      <Button
        variant="secondary"
        onClick={() => {
          bells({
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
          })
        }}
      >
        Render bell
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            toastOptions: {
              toasterId: "bells-toaster",
            },
            html: "Successfully <b class='text-red-500'>duplicated</b> project to <em class='text-blue-500'>My New Project</em>! üéâ",
            icon: <LayoutWallpaper />,
          })
        }}
      >
        Bell with HTML
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
          })
        }}
      >
        With icon
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
            onClose: (id) => {
              sonnerToast.dismiss(id)
            },
          })
        }}
      >
        With close button
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
            onClose: (id) => {
              sonnerToast.dismiss(id)
            },
            action: (id) => (
              <Chip
                size="medium"
                className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                onClick={() => {
                  sonnerToast.dismiss(id)
                }}
              >
                Action
              </Chip>
            ),
          })
        }}
      >
        With close button and action
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
            onClose: (id) => {
              sonnerToast.dismiss(id)
            },
            action: (id) => (
              <>
                <Chip
                  size="medium"
                  className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                  onClick={() => {
                    sonnerToast.dismiss(id)
                  }}
                >
                  Action
                </Chip>
                <Chip
                  size="medium"
                  className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                  onClick={() => {
                    sonnerToast.dismiss(id)
                  }}
                >
                  Dismiss
                </Chip>
              </>
            ),
            progress: true,
          })
        }}
      >
        With progress
      </Button>
    </div>
  );
}

import { bells } from "@choice-ui/bells"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <Toaster id="bells-main" />
