        id="bells-secondary"
        offset={{ bottom: "128px" }}
      />

      <div className="flex flex-col gap-2">
        <h3 className="text-body-large-strong">Main Toaster</h3>
        <div className="flex gap-2">
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-main",
                text: "This bell appears in the main toaster at bottom-center.",
              })
            }}
          >
            Main Toaster
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-main",
                icon: <LayoutWallpaper />,
                text: "Bell with icon and close button",
                onClose: (id) => {
                  sonnerToast.dismiss(id)
                },
              })
            }}
          >
            With Close
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-main",
                variant: "success",
                icon: "‚úÖ",
                text: "Success bell in main toaster",
                progress: true,
              })
            }}
          >
            With Progress
          </Button>
        </div>
      </div>

      <div className="flex flex-col gap-2">
        <h3 className="text-body-large-strong">Secondary Toaster</h3>
        <div className="flex gap-2">
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-secondary",
                icon: <LayoutWallpaper />,
                text: "This bell appears in the secondary toaster at top-center.",
              })
            }}
          >
            Secondary Toaster
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-secondary",
                variant: "danger",
                icon: "‚ö†Ô∏è",
                html: "Danger bell with <strong>HTML</strong> content",
                onClose: (id) => {
                  sonnerToast.dismiss(id)
                },
              })
            }}
          >
            Danger Variant
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-secondary",
                variant: "accent",
                icon: <LayoutWallpaper />,
                text: "Bell with action button",
                action: (id) => (
                  <Chip
                    size="medium"
                    className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                    onClick={() => {
                      sonnerToast.dismiss(id)
                    }}
                  >
                    Action
                  </Chip>
                ),
              })
            }}
          >
            With Action
          </Button>
        </div>
      </div>

      <div className="text-secondary-foreground text-body-small">
        <p className="font-strong">Usage:</p>
        <p>
          Multiple Toasters allow you to separate bells by context or priority. Each Toaster can
          have its own position, offset, and configuration. Use the <code>toasterId</code> prop to
          target a specific Toaster when calling <code>bells()</code>.
        </p>
      </div>
    </div>
  );
}

import { bells } from "@choice-ui/bells"

export default function App() {
  return (
    <div className="flex gap-2 capitalize">
      <Toaster />
      {colors.map((color) => (
        <Button
          key={color.color}
          variant="secondary"
          onClick={() => {
            bells({
              variant: color.color as
                | "default"
                | "accent"
                | "success"
                | "warning"
                | "danger"
                | "assistive",
              icon: <LayoutWallpaper />,
              text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
              onClose: (id) => {
                sonnerToast.dismiss(id)
              },
              action: (id) => (
                <Chip
                  size="medium"
                  className={tcx(
                    "flex-none",
                    color.color === "default"
                      ? "border-menu-boundary bg-transparent hover:bg-white/5"
                      : "border-black/10 bg-black/40 shadow-sm hover:bg-black/50",
                  )}
                  onClick={() => {
                    sonnerToast.dismiss(id)
                  }}
                >
                  {color.color}
                </Chip>
              ),
              progress: true,
            })
          }}
        >
          {color.color} color
        </Button>
      ))}
    </div>
  );
}

import { PREVIEW_KEYFRAMES, BezierCurveEditor } from "@choice-ui/bezier-curve-editor"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <style>{PREVIEW_KEYFRAMES(208 - 24 - 8).bezierLoop}</style>
      <div className="flex aspect-square w-52 items-center justify-center rounded-md border">
        <BezierCurveEditor
          size={96}
          allowNodeEditing={false}
          value={value}
          onChange={setValue}
          className="absolute"
        />
      </div>
      <div className="flex w-full items-center gap-2">
        <p className="flex-1">Bezier</p>
        <NumericInput
          className="w-full"
          value={value}
          expression="{v1},{v2},{v3},{v4}"
          onChange={(_val, detail) => {
            setValue(detail.array.slice(0, 4) as BezierCurveValueType)
          }}
        />
      </div>

      <div className="flex flex-col gap-2">
        <div className="bg-secondary-background relative h-8 w-full rounded-lg">
          <div
            className="absolute top-1 left-1 size-6 rounded-md bg-white shadow-xs"
            style={previewAnimationStyle}
          />
        </div>
        <div className="text-default-foreground/70 flex items-center gap-2 text-xs">
          <span>Speed:</span>
          <span className="text-blue-600">‚óè</span>
          <span>Slow</span>
          <span className="text-green-600">‚óè</span>
          <span>Medium</span>
          <span className="text-red-600">‚óè</span>
          <span>Fast</span>
        </div>
        <div
          className="h-1 w-full rounded-md"
          style={{
            backgroundImage: generateSpeedGradient(value),
          }}
        />
      </div>
    </div>
  );
}

