import { Button } from "@choice-ui/button"

export default function App() {
  return (
    <div
      className={tcx(
        "flex flex-col items-start gap-4 rounded-xl p-4",
        variant === Variant.Dark && "bg-gray-800",
      )}
    >
      <div className="flex gap-4">
        <Select
          value={variant}
          onChange={(value) => setVariant(value as Variant)}
        >
          <Select.Trigger>{variant}</Select.Trigger>
          <Select.Content>
            {Object.values(Variant).map((variant) => (
              <Select.Item
                key={variant}
                value={variant}
              >
                {variant}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
        <Select
          value={size}
          onChange={(value) => setSize(value as Size)}
        >
          <Select.Trigger>{size}</Select.Trigger>
          <Select.Content>
            {Object.values(Size).map((size) => (
              <Select.Item
                key={size}
                value={size}
              >
                {size}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
      </div>

      <div className="grid grid-cols-[auto_1fr_1fr] items-center gap-4">
        {Object.values(State).map((state) => (
          <Fragment key={state}>
            <span className="text-pink-500 capitalize">{state}</span>
            <Button
              variant={variant}
              size={size}
              active={state === State.Active}
              disabled={state === State.Disabled}
              loading={state === State.Loading}
              focused={state === State.Focused}
            >
              Button
            </Button>
            <Button
              variant={variant}
              size={size}
              active={state === State.Active}
              disabled={state === State.Disabled}
              loading={state === State.Loading}
              focused={state === State.Focused}
            >
              <SearchSmall />
              Button
            </Button>
          </Fragment>
        ))}
      </div>
    </div>
  );
}

