      <VirtualizedGrid
        enablePooling={true}
        poolSize={50}
        maxPoolSize={200}
        overscan={2}
        items={items}
        columnCount={(width, gap) => Math.max(1, Math.floor((width + gap) / (200 + gap)))}
        gridGap={() => 12}
        itemData={(item) => ({
          key: item.id,
          height: 200,
        })}
        renderItem={(item, index) => (
          <div className="bg-default-background rounded-lg border p-3 transition-shadow hover:shadow-md">
