export const Basic: Story = {
  render: () => <Button>Button</Button>,
};

/**
 * Sizes: Demonstrates the different size options for the Button component.
 * - Shows default and large button sizes.
 * - Useful for adapting buttons to different UI layouts or prominence.
 */

export const Sizes: Story = {
  render: () => (
    <>
      <Button>Default</Button>
      <Button size="large">Large</Button>
    </>
  ),
};

/**
 * Variants: Demonstrates all visual variants, sizes, and states for the Button component.
 * - Shows how to use the variant, size, and state props.
 * - Includes interactive controls for switching variants and sizes.
 * - Demonstrates active, focused, loading, and disabled states.
 * - Shows usage with and without icons.
 * - Useful for previewing all button styles and states in your design system.
 */

export const Variants: Story = {
  render: function Variants() {
    enum Variant {
      Dark = "dark",
      Destructive = "destructive",
      Ghost = "ghost",
      Inverse = "inverse",
      Link = "link",
      LinkDanger = "link-danger",
      Primary = "primary",
      Secondary = "secondary",
      SecondaryDestruct = "secondary-destruct",
      Solid = "solid",
      Success = "success",
    }

    enum Size {
      Default = "default",
      Large = "large",
    }

    enum State {
      Active = "active",
      Disabled = "disabled",
      Focused = "focused",
      Loading = "loading",
      Rest = "rest",
    }

    const [variant, setVariant] = useState<Variant>(Variant.Primary);
    const [size, setSize] = useState<Size>(Size.Default);

    return (
      <div
        className={tcx(
          "flex flex-col items-start gap-4 rounded-xl p-4",
          variant === Variant.Dark && "bg-gray-800"
        )}
      >
        <div className="flex gap-4">
          <Select
            value={variant}
            onChange={(value) => setVariant(value as Variant)}
          >
            <Select.Trigger>{variant}</Select.Trigger>
            <Select.Content>
              {Object.values(Variant).map((variant) => (
                <Select.Item key={variant} value={variant}>
                  {variant}
                </Select.Item>
              ))}
            </Select.Content>
          </Select>
          <Select value={size} onChange={(value) => setSize(value as Size)}>
            <Select.Trigger>{size}</Select.Trigger>
            <Select.Content>
              {Object.values(Size).map((size) => (
                <Select.Item key={size} value={size}>
                  {size}
                </Select.Item>
              ))}
            </Select.Content>
          </Select>
        </div>

        <div className="grid grid-cols-[auto_1fr_1fr] items-center gap-4">
          {Object.values(State).map((state) => (
            <Fragment key={state}>
              <span className="text-pink-500 capitalize">{state}</span>
              <Button
                variant={variant}
                size={size}
                active={state === State.Active}
                disabled={state === State.Disabled}
                loading={state === State.Loading}
                focused={state === State.Focused}
              >
                Button
              </Button>
              <Button
                variant={variant}
                size={size}
                active={state === State.Active}
                disabled={state === State.Disabled}
                loading={state === State.Loading}
                focused={state === State.Focused}
              >
                <SearchSmall />
                Button
              </Button>
            </Fragment>
          ))}
        </div>
      </div>
    );
  },
};

/**
 * AsChild: Demonstrates rendering the Button as another element using the `asChild` prop.
 * - Useful for rendering a button as a link or other custom element while retaining button styles and behaviors.
 */

export const AsChild: Story = {
  render: () => (
    <>
      <Button asChild>
        <a href="#">Link</a>
      </Button>
    </>
  ),
};

/**
 * Tooltip: Demonstrates the Button with a tooltip.
 * - Shows how to add a tooltip for additional context or guidance.
 * - Tooltips are accessible and appear on hover or focus.
 */

export const Tooltip: Story = {
  render: () => <Button tooltip={{ content: "Tooltip" }}>Button</Button>,
};

/**
 * Button component in readOnly state.
 *
 * In readOnly mode:
 * - The button does not respond to click events
 * - Mouse and touch events are blocked
 * - Useful for displaying buttons without allowing interactions
 */

export const Readonly: Story = {
  render: function ReadonlyStory() {
    const [clickCount, setClickCount] = useState(0);

    return (
      <div className="flex flex-col gap-4">
        <div className="rounded-xl border bg-stone-50 p-4">
          <div className="text-body-small-strong mb-2 text-stone-700">
            Click Count:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {clickCount}
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <Button readOnly onClick={() => setClickCount((prev) => prev + 1)}>
            Readonly Button
          </Button>
          <Button
            readOnly
            variant="secondary"
            onClick={() => setClickCount((prev) => prev + 1)}
          >
            Readonly Secondary
          </Button>
          <Button onClick={() => setClickCount((prev) => prev + 1)}>
            Normal Button (for comparison)
          </Button>
        </div>

        <div className="text-body-small text-stone-600">
          ðŸ’¡ Try clicking on the readOnly buttons - the click count should not
          change. Only the normal button will increment the count.
        </div>
      </div>
    );
  },
};

export const Basic: Story = {
  render: function BasicStory() {
    return (
      <IconButton>
        <FieldTypeButton />
      </IconButton>
    );
  },
};

/**
 * Variants: Demonstrates all visual variants, sizes, and states of the IconButton component.
 *
 * Features:
 * - Interactive controls to switch between variants and sizes
 * - Displays all possible states (rest, active, focused, loading, disabled)
 * - Shows consistent styling across all combinations
 *
 * Variants:
 * - `default`: Standard styling with hover and active states
 * - `secondary`: Subdued styling for less prominent actions
 * - `solid`: Filled background for primary or important actions
 * - `highlight`: Visually distinctive for drawing attention
 *
 * Sizes:
 * - `default`: Standard size for most interfaces
 * - `large`: Larger size for touch targets or emphasis
 */

export const Variants: Story = {
  render: function Variants() {
    enum Variant {
      Dark = "dark",
      Default = "default",
      Ghost = "ghost",
      Highlight = "highlight",
      Secondary = "secondary",
      Solid = "solid",
    }

    enum Size {
      Default = "default",
      Large = "large",
    }

    enum State {
      Active = "active",
      Disabled = "disabled",
      Focused = "focused",
      Loading = "loading",
      Rest = "rest",
    }

    const [variant, setVariant] = useState<Variant>(Variant.Default);
    const [size, setSize] = useState<Size>(Size.Default);

    return (
      <div
        className={tcx(
          "flex flex-col items-start gap-4 rounded-xl p-4",
          variant === Variant.Dark && "bg-gray-800"
        )}
      >
        <div className="flex gap-4">
          <Select
            value={variant}
            onChange={(value) => setVariant(value as Variant)}
          >
            <Select.Trigger>{variant}</Select.Trigger>
            <Select.Content>
              {Object.values(Variant).map((variant) => (
                <Select.Item key={variant} value={variant}>
                  {variant}
                </Select.Item>
              ))}
            </Select.Content>
          </Select>
          <Select value={size} onChange={(value) => setSize(value as Size)}>
            <Select.Trigger>{size}</Select.Trigger>
            <Select.Content>
              {Object.values(Size).map((size) => (
                <Select.Item key={size} value={size}>
                  {size}
                </Select.Item>
              ))}
            </Select.Content>
          </Select>
        </div>

        <div className="grid grid-cols-[auto_1fr] items-center gap-4">
          {Object.values(State).map((state) => (
            <Fragment key={state}>
              <span className="text-pink-500 capitalize">{state}</span>
              <IconButton
                variant={variant}
                size={size}
                active={state === State.Active}
                disabled={state === State.Disabled}
                loading={state === State.Loading}
                focused={state === State.Focused}
              >
                <FieldTypeRating />
              </IconButton>
            </Fragment>
          ))}
        </div>
      </div>
    );
  },
};

/**
 * Tooltip: Demonstrates adding descriptive tooltips to IconButtons.
 *
 * Features:
 * - Helpful text appears on hover/focus
 * - Improves accessibility and usability
 * - Clarifies the button's purpose when icons alone may be ambiguous
 *
 * Best Practices:
 * - Use concise, clear tooltip text
 * - Describe the action, not the icon
 * - Consider tooltip placement based on button position
 */

export const AsChild: Story = {
  render: () => (
    <IconButton asChild>
      <a href="#">
        <FieldTypeAttachment />
      </a>
    </IconButton>
  ),
};

/**
 * IconButton component in readOnly state.
 *
 * In readOnly mode:
 * - The button does not respond to click events
 * - Useful for displaying icon buttons without allowing interactions
 */

export const Readonly: Story = {
  render: function ReadonlyStory() {
    const [clickCount, setClickCount] = useState(0);

    return (
      <div className="flex flex-col gap-4">
        <div className="rounded-xl border bg-stone-50 p-4">
          <div className="text-body-small-strong mb-2 text-stone-700">
            Click Count:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {clickCount}
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <IconButton
            readOnly
            onClick={() => setClickCount((prev) => prev + 1)}
          >
            <FieldTypeAttachment />
          </IconButton>
          <IconButton
            readOnly
            variant="secondary"
            onClick={() => setClickCount((prev) => prev + 1)}
          >
            <FieldTypeCheckbox />
          </IconButton>
          <IconButton onClick={() => setClickCount((prev) => prev + 1)}>
            <FieldTypeCount />
          </IconButton>
        </div>

        <div className="text-body-small text-stone-600">
          ðŸ’¡ Try clicking on the readonly icon buttons - the click count should
          not change. Only the normal button will increment the count.
        </div>
      </div>
    );
  },
};

export const AsButton: Story = {
  render: () => (
    <LinkButton onClick={() => alert("Button clicked!")}>Click me</LinkButton>
  ),
};

/**
 * Basic link usage with href prop.
 * Renders as an <a> element for internal navigation.
 */

export const AsInternalLink: Story = {
  render: () => <LinkButton href="/dashboard">Go to Dashboard</LinkButton>,
};

/**
 * External link automatically gets security attributes.
 * target="_blank" and rel="noopener noreferrer" are added automatically.
 */

export const AsExternalLink: Story = {
  render: () => <LinkButton href="https://github.com">Visit GitHub</LinkButton>,
};

/**
 * Default variant with standard accent color styling.
 */

export const Default: Story = {
  render: () => (
    <LinkButton href="/example" variant="default">
      Default Style
    </LinkButton>
  ),
};

/**
 * Subtle variant with muted styling.
 */

export const Subtle: Story = {
  render: () => (
    <LinkButton href="/example" variant="subtle">
      Subtle Style
    </LinkButton>
  ),
};

/**
 * Disabled state prevents interaction and shows visual feedback.
 * Works for both links and buttons.
 */

export const Disabled: Story = {
  render: () => (
    <div className="flex gap-4">
      <LinkButton disabled href="/example">
        Disabled Link
      </LinkButton>
      <LinkButton disabled onClick={() => alert("Won't fire")}>
        Disabled Button
      </LinkButton>
    </div>
  ),
};

/**
 * Links with icons for better visual hierarchy.
 */

export const AllVariants: Story = {
  render: () => (
    <div className="flex gap-4">
      <LinkButton href="/example" variant="default">
        Default
      </LinkButton>
      <LinkButton href="/example" variant="subtle">
        Subtle
      </LinkButton>
    </div>
  ),
};

/**
 * Security features demonstration.
 * External links automatically get safety attributes.
 */

export const Basic: Story = {
  render: function BasicStory() {
    const [value, setValue] = useState("sun");
    return (
      <Segmented value={value} onChange={(value) => setValue(value)}>
        <Segmented.Item value="sun" aria-label="Sun">
          <FieldTypeAttachment />
        </Segmented.Item>
        <Segmented.Item value="moon" aria-label="Moon">
          <FieldTypeCheckbox />
        </Segmented.Item>
        <Segmented.Item value="system" aria-label="System">
          <FieldTypeCount />
        </Segmented.Item>
      </Segmented>
    );
  },
};

/**
 * ### Disabled Options
 *
 * Use the `disabled` property to disable specific segments in the control:
 * - Disabled options cannot be selected
 * - They display a visual disabled state
 * - They're excluded from keyboard navigation
 * - Screen readers announce them as disabled
 */

export const Disabled: Story = {
  render: function DisabledStory() {
    const [value, setValue] = useState("right");
    return (
      <Segmented value={value} onChange={(value) => setValue(value)}>
        <Segmented.Item value="left" disabled className="px-2">
          Left
        </Segmented.Item>
        <Segmented.Item value="center" disabled className="px-2">
          Center
        </Segmented.Item>
        <Segmented.Item value="right" disabled className="px-2">
          Right
        </Segmented.Item>
      </Segmented>
    );
  },
};

/**
 * ### Custom Content Styling
 *
 * The component provides flexible styling options:
 *
 * - Use the `className` property to directly style individual options
 * - Use `classNames.container` to style the outer container
 *
 * #### Best Practices
 * - For text content, add horizontal padding using `px-{size}`
 * - For icon + text combinations, use `gap-{size}` to control spacing
 * - Keep padding consistent across all options for visual alignment
 */

export const WithTooltip: Story = {
  render: function WithTooltipStory() {
    const [value, setValue] = useState("sun");
    return (
      <Segmented value={value} onChange={(value) => setValue(value)}>
        <Segmented.Item
          value="sun"
          tooltip={{
            content: "Sun",
          }}
        >
          <FieldTypeAttachment />
        </Segmented.Item>
        <Segmented.Item
          value="moon"
          tooltip={{
            content: "Moon",
          }}
        >
          <FieldTypeCheckbox />
        </Segmented.Item>
        <Segmented.Item
          value="system"
          tooltip={{
            content: "System",
          }}
        >
          <FieldTypeCount />
        </Segmented.Item>
      </Segmented>
    );
  },
};

/**
 * ### Visual Variants
 *
 * Demonstrates different visual variants of the segmented control:
 *
 * - **default**: Follows the page theme dynamically (light/dark mode)
 * - **light**: Fixed light appearance regardless of theme
 * - **dark**: Fixed dark appearance regardless of theme
 * - **reset**: Removes variant styling, no variant settings applied
 *
 * Each variant maintains full functionality while adapting its visual style
 * to different contexts and themes.
 */

export const Readonly: Story = {
  render: function ReadonlyStory() {
    const [value, setValue] = useState<string>("option1");
    const [changeCount, setChangeCount] = useState(0);

    const handleChange = (newValue: string) => {
      setValue(newValue);
      setChangeCount((prev) => prev + 1);
    };

    return (
      <div className="flex flex-col gap-4">
        <div className="rounded-xl border bg-stone-50 p-4">
          <div className="text-body-small-strong mb-2 text-stone-700">
            Current Value:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {value}
          </div>
          <div className="text-body-small-strong mt-2 text-stone-700">
            Change Count:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {changeCount}
          </div>
        </div>

        <Segmented readOnly value={value} onChange={handleChange}>
          <Segmented.Item value="option1">Option 1</Segmented.Item>
          <Segmented.Item value="option2">Option 2</Segmented.Item>
          <Segmented.Item value="option3">Option 3</Segmented.Item>
        </Segmented>

        <Segmented value={value} onChange={handleChange}>
          <Segmented.Item value="option1">Option 1</Segmented.Item>
          <Segmented.Item value="option2">Option 2</Segmented.Item>
          <Segmented.Item value="option3">Option 3</Segmented.Item>
        </Segmented>

        <div className="text-body-small text-stone-600">
          ðŸ’¡ Try clicking on the readonly segmented control - the value should
          not change and the change count should remain at 0. Only the normal
          control will change the value.
        </div>
      </div>
    );
  },
};

export const Basic: Story = {
  render: function ControlledStory() {
    const [controlled, setControlled] = useState(false);
    return (
      <ToggleButton
        aria-label="Toggle button"
        value={controlled}
        onChange={(value) => setControlled(value)}
      >
        {controlled ? <FieldTypeCheckbox /> : <FieldAdd />}
      </ToggleButton>
    );
  },
};

/**
 * The `variant` prop is used to set the variant of the toggle button.
 * - `default`: default variant
 * - `secondary`: secondary variant
 * - `highlight`: highlight variant
 */

export const Variants: Story = {
  render: function VariantsStory() {
    enum Variant {
      Default = "default",
      Highlight = "highlight",
      Secondary = "secondary",
    }

    enum Size {
      Default = "default",
      Large = "large",
    }

    enum State {
      Active = "active",
      Disabled = "disabled",
      Focused = "focused",
      Rest = "rest",
    }

    enum Event {
      Click = "click",
      Mousedown = "mousedown",
    }

    const [variant, setVariant] = useState<Variant>(Variant.Default);
    const [size, setSize] = useState<Size>(Size.Default);
    const [controlled, setControlled] = useState(false);
    const [event, setEvent] = useState<Event>(Event.Click);
    return (
      <div className="flex flex-col items-start gap-4">
        <div className="flex gap-4">
          <Select
            value={variant}
            onChange={(value) => setVariant(value as Variant)}
          >
            <Select.Trigger>{variant}</Select.Trigger>
            <Select.Content>
              {Object.values(Variant).map((variant) => (
                <Select.Item key={variant} value={variant}>
                  {variant}
                </Select.Item>
              ))}
            </Select.Content>
          </Select>
          <Select value={size} onChange={(value) => setSize(value as Size)}>
            <Select.Trigger>{size}</Select.Trigger>
            <Select.Content>
              {Object.values(Size).map((size) => (
                <Select.Item key={size} value={size}>
                  {size}
                </Select.Item>
              ))}
            </Select.Content>
          </Select>
          <Select value={event} onChange={(value) => setEvent(value as Event)}>
            <Select.Trigger>{event}</Select.Trigger>
            <Select.Content>
              {Object.values(Event).map((event) => (
                <Select.Item key={event} value={event}>
                  {event}
                </Select.Item>
              ))}
            </Select.Content>
          </Select>
        </div>

        <div className="grid grid-cols-[auto_1fr] items-center gap-4">
          {Object.values(State).map((state) => (
            <Fragment key={state}>
              <span className="text-fuchsia-500 capitalize">{state}</span>
              <ToggleButton
                variant={variant}
                value={controlled}
                onChange={(value) => setControlled(value)}
                size={size}
                active={state === State.Active}
                focused={state === State.Focused}
                disabled={state === State.Disabled}
                event={event}
              >
                {controlled ? <FieldTypeCheckbox /> : <FieldTypeAttachment />}
              </ToggleButton>
            </Fragment>
          ))}
        </div>
      </div>
    );
  },
};

/**
 * The `tooltip` prop is used to show a tooltip when the toggle button is hovered.
 * - The `aria-label` will be used as the tooltip's content if the content type is string.
 */

export const Tooltip: Story = {
  render: function TooltipStory() {
    const [controlled, setControlled] = useState(false);
    return (
      <ToggleButton
        tooltip={{
          content: controlled
            ? "Current state is correct"
            : "Current state is wrong",
        }}
        value={controlled}
        onChange={(value) => setControlled(value)}
      >
        {controlled ? <FieldTypeCheckbox /> : <FieldAdd />}
      </ToggleButton>
    );
  },
};

/**
 * ToggleButton component in readOnly state.
 *
 * In readOnly mode:
 * - The toggle button does not respond to click or change events
 * - The value cannot be changed
 * - Useful for displaying toggle state without allowing changes
 */

export const Readonly: Story = {
  render: function ReadonlyStory() {
    const [value, setValue] = useState(false);
    const [changeCount, setChangeCount] = useState(0);

    const handleChange = (newValue: boolean) => {
      setValue(newValue);
      setChangeCount((prev) => prev + 1);
    };

    return (
      <div className="flex flex-col gap-4">
        <div className="rounded-xl border bg-stone-50 p-4">
          <div className="text-body-small-strong mb-2 text-stone-700">
            Current Value:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {value ? "true" : "false"}
          </div>
          <div className="text-body-small-strong mt-2 text-stone-700">
            Change Count:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {changeCount}
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <ToggleButton readOnly value={value} onChange={handleChange}>
            {value ? <FieldTypeCheckbox /> : <FieldAdd />}
          </ToggleButton>
          <ToggleButton
            readOnly
            value={!value}
            onChange={handleChange}
            variant="secondary"
          >
            {!value ? <FieldTypeCheckbox /> : <FieldAdd />}
          </ToggleButton>
          <ToggleButton value={value} onChange={handleChange}>
            {value ? <FieldTypeCheckbox /> : <FieldAdd />}
          </ToggleButton>
        </div>

        <div className="text-body-small text-stone-600">
          ðŸ’¡ Try clicking on the readonly toggle buttons - the value should not
          change and the change count should remain at 0. Only the normal button
          will change the value.
        </div>
      </div>
    );
  },
};

export const Basic: Story = {
  render: function BasicStory() {
    const [value, setValue] = useState("");
    const [triggerType, setTriggerType] = useState<"click" | "focus" | "input">(
      "input"
    );

    const itemsToShow = useMemo(() => {
      if (triggerType === "click") {
        // ç‚¹å‡»triggeræ—¶æ˜¾ç¤ºæ‰€æœ‰items
        return fruits;
      }
      if (!value.trim()) {
        return [];
      }
      // è¾“å…¥æˆ–focusæ—¶æ˜¾ç¤ºè¿‡æ»¤åŽçš„items
      return fruits.filter((fruit) =>
        fruit.toLowerCase().startsWith(value.toLowerCase())
      );
    }, [value, triggerType]);

    const handleChange = useEventCallback((newValue: string) => {
      setValue(newValue);
      setTriggerType("input");
    });

    const handleOpenChange = useEventCallback(
      (open: boolean, trigger: "click" | "focus" | "input" = "input") => {
        if (open) {
          setTriggerType(trigger);
        }
      }
    );

    return (
      <div className="w-64">
        <Combobox
          value={value}
          onChange={handleChange}
          onOpenChange={handleOpenChange}
        >
          <Combobox.Trigger placeholder="Search fruits..." />
          {itemsToShow.length > 0 && (
            <Combobox.Content>
              <>
                <Combobox.Label>Fruits</Combobox.Label>
                {itemsToShow.map((fruit) => (
                  <Combobox.Item key={fruit} onClick={() => setValue(fruit)}>
                    <Combobox.Value>{fruit}</Combobox.Value>
                  </Combobox.Item>
                ))}
              </>
            </Combobox.Content>
          )}
        </Combobox>
      </div>
    );
  },
};

/**
 * Clearable: Combobox with clearable input.
 * - Shows clear button when value is not empty
 * - Clear button is hidden when value is empty
 */

export const Clearable: Story = {
  render: function ClearableStory() {
    const [value, setValue] = useState("");

    const filteredFruits = useMemo(() => {
      if (!value.trim()) return [];
      return fruits.filter((fruit) =>
        fruit.toLowerCase().startsWith(value.toLowerCase())
      );
    }, [value]);

    return (
      <div className="w-64">
        <Combobox value={value} onChange={setValue}>
          <Combobox.Trigger showClear placeholder="Search fruits..." />
          {filteredFruits.length > 0 && (
            <Combobox.Content>
              <>
                <Combobox.Label>Fruits</Combobox.Label>
                {filteredFruits.map((fruit) => (
                  <Combobox.Item key={fruit} onClick={() => setValue(fruit)}>
                    <Combobox.Value>{fruit}</Combobox.Value>
                  </Combobox.Item>
                ))}
              </>
            </Combobox.Content>
          )}
        </Combobox>
      </div>
    );
  },
};

/**
 * Controlled: Controlled combobox with external state management.
 * - Value is controlled by parent component
 * - Demonstrates integration with forms
 */

export const Controlled: Story = {
  render: function ControlledStory() {
    const [selectedFruit, setSelectedFruit] = useState("Apple");
