/**
 * Mentions with Slate.js - Combobox integrated with rich text editor for mentions
 */

export const MentionsWithSlate: Story = {
  render: function MentionsWithSlateStory() {
    const [isOpen, setIsOpen] = useState(false);
    const [position, setPosition] = useState<{ x: number; y: number } | null>(
      null
    );
    const [mentionQuery, setMentionQuery] = useState("");
    const editorRef = useRef<HTMLDivElement>(null);

    const [enterFilter, setEnterFilter] = useState(true);

    // åˆ›å»º Slate ç¼–è¾‘å™¨å®žä¾‹
    const editor = useMemo(() => withReact(createEditor()), []);

    // åˆå§‹å€¼
    const initialValue: Descendant[] = [
      {
        type: "paragraph",
        children: [{ text: "" }],
      } as Descendant,
    ];
    const [value, setValue] = useState<Descendant[]>(initialValue);

    // æ›´å¤šç”¨æˆ·æ•°æ®ç”¨äºŽæµ‹è¯•è¿‡æ»¤
    const allUsers = useMemo(
      () =>
        Array.from({ length: 100 }, (_, index) => ({
          id: index.toString(),
          name: faker.person.fullName(),
          username: faker.internet.userName(),
          role: faker.person.jobTitle(),
          avatar: `https://api.dicebear.com/7.x/avataaars/svg?seed=${faker.person.fullName()}`,
        })),
      []
    );

    // è¿‡æ»¤ç”¨æˆ·åˆ—è¡¨ - æ¨¡æ‹Ÿæ­£å¸¸ Combobox çš„è¿‡æ»¤é€»è¾‘
    const filteredUsers = useMemo(() => {
      if (enterFilter) {
        // ç±»ä¼¼æ­£å¸¸æ¨¡å¼ï¼šå¦‚æžœæ²¡æœ‰æŸ¥è¯¢å†…å®¹ï¼Œä¸æ˜¾ç¤ºä»»ä½•é€‰é¡¹
        if (!mentionQuery.trim()) return [];

        return allUsers.filter(
          (user) =>
            user.name.toLowerCase().includes(mentionQuery.toLowerCase()) ||
            user.username.toLowerCase().includes(mentionQuery.toLowerCase()) ||
            user.role.toLowerCase().includes(mentionQuery.toLowerCase())
        );
      } else {
        return allUsers;
      }
    }, [mentionQuery, allUsers, enterFilter]);

    // èŽ·å–ç¼–è¾‘å™¨æ–‡æœ¬å†…å®¹
    const getEditorText = useCallback(() => {
      return value.map((n) => Node.string(n)).join("\n");
    }, [value]);

    // å¤„ç† Combobox çš„æŸ¥è¯¢å˜åŒ–ï¼ˆä»…ç”¨äºŽåæ ‡æ¨¡å¼ä¸‹çš„å†…éƒ¨çŠ¶æ€åŒæ­¥ï¼‰
    const handleComboboxQueryChange = useCallback((query: string) => {
      // åœ¨åæ ‡æ¨¡å¼ä¸‹ï¼Œæˆ‘ä»¬ä¸è®© Combobox æŽ§åˆ¶æŸ¥è¯¢ï¼Œè€Œæ˜¯ç”± Slate ç¼–è¾‘å™¨æŽ§åˆ¶
      // è¿™ä¸ªå›žè°ƒé€šå¸¸ä¸ä¼šè¢«è°ƒç”¨ï¼Œä½†ä¸ºäº†å®Œæ•´æ€§ä¿ç•™
    }, []);

    // å¤„ç†ç¼–è¾‘å™¨å†…å®¹å˜åŒ–
    const handleChange = useCallback((newValue: Descendant[]) => {
      setValue(newValue);

      const text = newValue.map((n) => Node.string(n)).join("\n");
      const lastAtIndex = text.lastIndexOf("@");

      // æ£€æŸ¥ @ æ˜¯å¦å­˜åœ¨ï¼Œå¹¶ä¸” @ åŽé¢æ²¡æœ‰ç©ºæ ¼æˆ–è€…æ˜¯æ–‡æœ¬çš„æœ«å°¾
      if (lastAtIndex !== -1) {
        const afterAt = text.substring(lastAtIndex + 1);
        const hasSpaceAfterAt = afterAt.includes(" ") || afterAt.includes("\n");

        if (!hasSpaceAfterAt) {
          // æå–æŸ¥è¯¢å­—ç¬¦ä¸²ï¼ˆ@ åŽé¢çš„å†…å®¹ï¼‰
          setMentionQuery(afterAt);

          // èŽ·å–ç¼–è¾‘å™¨ä½ç½®
          const domSelection = window.getSelection();
          if (domSelection && domSelection.rangeCount > 0) {
            const range = domSelection.getRangeAt(0);
            const rect = range.getBoundingClientRect();
            setPosition({
              x: rect.left,
              y: rect.bottom + 4,
            });
          } else if (editorRef.current) {
            // å¤‡é€‰æ–¹æ¡ˆï¼šä½¿ç”¨ç¼–è¾‘å™¨å®¹å™¨ä½ç½®
            const rect = editorRef.current.getBoundingClientRect();
            setPosition({
              x: rect.left,
              y: rect.bottom + 4,
            });
          }
          setIsOpen(true);
        } else {
          setIsOpen(false);
          setMentionQuery("");
        }
      } else {
        setIsOpen(false);
        setMentionQuery("");
      }
    }, []);

    // å¤„ç†é”®ç›˜äº‹ä»¶ - åœ¨èœå•æ‰“å¼€æ—¶æ‹¦æˆªå¯¼èˆªé”®
    const handleKeyDown = useCallback(
      (event: React.KeyboardEvent) => {
        if (!isOpen) return;

        // å¦‚æžœèœå•æ‰“å¼€ï¼Œæ‹¦æˆªä¸Šä¸‹ç®­å¤´å’Œå›žè½¦é”®
        if (
          event.key === "ArrowDown" ||
          event.key === "ArrowUp" ||
          event.key === "Enter" ||
          event.key === "Escape"
        ) {
          event.preventDefault();
          event.stopPropagation();

          // èŽ·å–èœå•çš„é”®ç›˜å¤„ç†å‡½æ•°ï¼ˆé€šè¿‡refè®¿é—®ï¼‰
          const menuElement = document.querySelector(
            '[role="listbox"]'
          ) as HTMLElement;
          if (menuElement) {
            // ç›´æŽ¥åœ¨èœå•å…ƒç´ ä¸Šè§¦å‘é”®ç›˜äº‹ä»¶
            const keyEvent = new KeyboardEvent("keydown", {
              key: event.key,
              code: event.code,
              ctrlKey: event.ctrlKey,
              shiftKey: event.shiftKey,
              altKey: event.altKey,
              metaKey: event.metaKey,
              bubbles: true,
              cancelable: true,
            });
            menuElement.dispatchEvent(keyEvent);
          }
        }
      },
      [isOpen]
    );

    // å¤„ç†ç”¨æˆ·é€‰æ‹©
    const handleSelectUser = useCallback(
      (user: (typeof allUsers)[0]) => {
        // ä½¿ç”¨ Slate çš„ API æ¥æ­£ç¡®æ’å…¥æåŠå†…å®¹
        const { selection } = editor;

        if (selection) {
          // èŽ·å–å½“å‰æ–‡æœ¬å’Œå…‰æ ‡ä½ç½®
          const text = getEditorText();
          const lastAtIndex = text.lastIndexOf("@");

          if (lastAtIndex !== -1) {
            // è®¡ç®—éœ€è¦æ›¿æ¢çš„èŒƒå›´
            const afterAtText = text.substring(lastAtIndex + 1);

            // åˆ›å»ºé€‰æ‹©èŒƒå›´ï¼Œä»Ž @ å¼€å§‹åˆ°å½“å‰å…‰æ ‡ä½ç½®
            const start = { path: [0, 0], offset: lastAtIndex };
            const end = {
              path: [0, 0],
              offset: lastAtIndex + 1 + afterAtText.length,
            };
            const range = { anchor: start, focus: end };

            // é€‰æ‹©è¦æ›¿æ¢çš„æ–‡æœ¬èŒƒå›´
            Transforms.select(editor, range);

            // æ’å…¥æåŠæ–‡æœ¬
            Transforms.insertText(editor, `@${user.name} `);
          }
        }

        setIsOpen(false);
        setMentionQuery("");
        // ä¿æŒç¼–è¾‘å™¨ç„¦ç‚¹
        ReactEditor.focus(editor);
      },
      [editor, getEditorText]
    );

    return (
      <div className="w-96 space-y-4">
        <div className="rounded-xl border p-4">
          <h3 className="font-strong mb-2">
            ðŸ” Combobox Mentions with Slate.js
          </h3>
          <p className="text-secondary-foreground text-body-small">
            Type @ and continue typing to filter users. Combobox maintains focus
            on the editor.
          </p>
        </div>

        <Checkbox value={enterFilter} onChange={setEnterFilter}>
          <Checkbox.Label>Enter filter</Checkbox.Label>
        </Checkbox>

        {/* Slate ç¼–è¾‘å™¨ */}
        <div
          ref={editorRef}
          className="focus-within:border-selected-boundary min-h-[100px] w-full rounded-lg border p-3"
        >
          <Slate
            editor={editor}
            initialValue={initialValue}
            onChange={handleChange}
          >
            <Editable
              placeholder="Type @ to mention someone, then continue typing to filter..."
              className="outline-none"
              style={{ minHeight: "76px" }}
              onKeyDown={handleKeyDown}
            />
          </Slate>
        </div>

        <Combobox
          trigger="coordinate" // æ˜Žç¡®æŒ‡å®šåæ ‡æ¨¡å¼
          position={position}
          value={mentionQuery}
          onChange={handleComboboxQueryChange}
          open={isOpen}
          onOpenChange={setIsOpen}
          placement="bottom-start"
          autoSelection={true} // å¯ç”¨è‡ªåŠ¨é€‰æ‹©
        >
          {filteredUsers.length > 0 && (
            <Combobox.Content>
              <Combobox.Label>
                {enterFilter
                  ? mentionQuery
                    ? `Search ${mentionQuery}`
                    : "Users"
                  : "Users"}
                {` (${filteredUsers.length})`}
              </Combobox.Label>

              {filteredUsers.map((user) => (
                <Combobox.Item
                  key={user.id}
                  onClick={() => handleSelectUser(user)}
                  prefixElement={
                    <img
                      src={user.avatar}
                      alt={user.name}
                      className="size-4 rounded-full"
                    />
                  }
                  suffixElement={
                    <span className="text-body-small text-white/60">
                      {user.role}
                    </span>
                  }
                >
                  <Combobox.Value>{user.name}</Combobox.Value>
                </Combobox.Item>
              ))}
            </Combobox.Content>
          )}
        </Combobox>

        {/* è°ƒè¯•ä¿¡æ¯ */}
        <div className="space-y-2">
          <div className="bg-secondary-background text-secondary-foreground rounded-xl p-4">
            <div>
              <strong>Current text:</strong> &ldquo;{getEditorText()}&rdquo;
            </div>
            <div>
              <strong>Mention query:</strong> &ldquo;{mentionQuery}&rdquo;
            </div>
            <div>
              <strong>Filtered users:</strong> {filteredUsers.length} /{" "}
              {allUsers.length}
            </div>
            <div>
              <strong>Menu open:</strong> {isOpen ? "Yes" : "No"}
            </div>
            <div>
              <strong>Position:</strong>{" "}
              {position ? `x:${position.x}, y:${position.y}` : "null"}
            </div>
            <div>
              <strong>Should show menu:</strong> {isOpen ? "Yes" : "No"}
            </div>
          </div>

          <div className="rounded-xl border p-4">
            <h4 className="font-strong mb-2">Test Cases:</h4>
            <ul className="text-secondary-foreground space-y-1">
              <li>â€¢ Type @ - Shows all 5 users</li>
              <li>â€¢ Type @john - Filters to John Doe, Alice Johnson</li>
              <li>â€¢ Type @dev - Filters to developers</li>
              <li>â€¢ Type @engineer - Filters to engineers</li>
              <li>â€¢ Type @xyz - Shows &ldquo;No users found&rdquo;</li>
              <li>â€¢ Add space after selection - Closes menu</li>
            </ul>
          </div>
        </div>
      </div>
    );
  },
};

