 */

export const WithSearch: Story = {
  render: function WithSearchStory() {
    const [search, setSearch] = useState("");
    const [selected, setSelected] = useState<string[]>([]);
    const [isOpen, setIsOpen] = useState(false);

    const allOptions = useMemo(
      () =>
        Array.from({ length: 8 }, (_, i) => ({
          id: `option-${i + 1}`,
          label: faker.music.genre(),
        })),
      []
    );

    const filteredOptions = useMemo(
      () =>
        allOptions.filter((option) =>
          option.label.toLowerCase().includes(search.toLowerCase())
        ),
      [allOptions, search]
    );

    useEffect(() => {
      if (!isOpen) {
        setSearch("");
      }
    }, [isOpen]);

    return (
      <Dropdown selection={true} open={isOpen} onOpenChange={setIsOpen}>
        <Dropdown.Trigger>
          <Dropdown.Value>
            Search Music ({selected.length} selected)
          </Dropdown.Value>
        </Dropdown.Trigger>
        <Dropdown.Content>
          <Dropdown.Search
            value={search}
            onChange={setSearch}
            placeholder="Search genres..."
          />
          <Dropdown.Divider />

          {filteredOptions.length > 0 ? (
            <>
              <Dropdown.Label>Music Genres</Dropdown.Label>
              {filteredOptions.map((option) => (
                <Dropdown.Item
                  key={option.id}
                  selected={selected.includes(option.id)}
                  onClick={() => {
                    setSelected((prev) =>
                      prev.includes(option.id)
                        ? prev.filter((id) => id !== option.id)
                        : [...prev, option.id]
                    );
                  }}
                >
                  <Dropdown.Value>{option.label}</Dropdown.Value>
                </Dropdown.Item>
              ))}
              {selected.length > 0 && (
                <>
                  <Dropdown.Divider />
                  <Dropdown.Button onClick={() => setSelected([])}>
                    Clear All Selections
                  </Dropdown.Button>
                </>
              )}
            </>
          ) : (
            <div className="flex h-32 flex-col items-center justify-center gap-2 text-center text-white/40">
              <Search width={24} height={24} />
              <span>No results found</span>
              <Button variant="link" onClick={() => setSearch("")}>
                Clear Search
              </Button>
            </div>
          )}
        </Dropdown.Content>
      </Dropdown>
    );
  },
};

