## React Hook Example

Here's a comprehensive custom hook implementation:

\`\`\`typescript
import { useState, useEffect, useCallback, useMemo, useRef } from 'react'

interface FetchState<T> {
  data: T | null
  loading: boolean
  error: Error | null
  refetch: () => Promise<void>
}

interface FetchOptions<T> {
  url: string
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE'
  body?: Record<string, unknown>
  headers?: Record<string, string>
  transform?: (data: unknown) => T
  onSuccess?: (data: T) => void
  onError?: (error: Error) => void
  enabled?: boolean
  refetchInterval?: number
  retryCount?: number
  retryDelay?: number
}

export function useFetch<T>(options: FetchOptions<T>): FetchState<T> {
  const {
    url,
    method = 'GET',
    body,
    headers = {},
    transform,
    onSuccess,
    onError,
    enabled = true,
    refetchInterval,
    retryCount = 3,
    retryDelay = 1000,
  } = options

  const [data, setData] = useState<T | null>(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<Error | null>(null)
  const retryCountRef = useRef(0)
  const abortControllerRef = useRef<AbortController | null>(null)

  const fetchData = useCallback(async () => {
    if (!enabled) return

    // Cancel previous request
    if (abortControllerRef.current) {
      abortControllerRef.current.abort()
    }
    abortControllerRef.current = new AbortController()

    setLoading(true)
    setError(null)

    try {
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          ...headers,
        },
        body: body ? JSON.stringify(body) : undefined,
        signal: abortControllerRef.current.signal,
      })

      if (!response.ok) {
        throw new Error(\`HTTP error! status: \${response.status}\`)
      }

      const rawData = await response.json()
      const transformedData = transform ? transform(rawData) : rawData as T

      setData(transformedData)
      onSuccess?.(transformedData)
      retryCountRef.current = 0
    } catch (err) {
      if (err instanceof Error && err.name === 'AbortError') {
        return // Ignore abort errors
      }

      const error = err instanceof Error ? err : new Error('Unknown error')

      if (retryCountRef.current < retryCount) {
        retryCountRef.current++
        setTimeout(fetchData, retryDelay * retryCountRef.current)
        return
      }

      setError(error)
      onError?.(error)
    } finally {
      setLoading(false)
    }
  }, [url, method, body, headers, transform, onSuccess, onError, enabled, retryCount, retryDelay])

  useEffect(() => {
    fetchData()

    return () => {
      abortControllerRef.current?.abort()
    }
  }, [fetchData])

  useEffect(() => {
    if (!refetchInterval || !enabled) return

    const intervalId = setInterval(fetchData, refetchInterval)
    return () => clearInterval(intervalId)
  }, [fetchData, refetchInterval, enabled])

  const memoizedState = useMemo(() => ({
    data,
    loading,
    error,
    refetch: fetchData,
  }), [data, loading, error, fetchData])

  return memoizedState
}
