## React Component Example

A complex form component with validation:

\`\`\`tsx
import React, { useState, useCallback, useEffect, FormEvent } from 'react'
import { motion, AnimatePresence } from 'framer-motion'

interface FormField {
  name: string
  type: 'text' | 'email' | 'password' | 'number' | 'textarea'
  label: string
  placeholder?: string
  required?: boolean
  minLength?: number
  maxLength?: number
  pattern?: RegExp
  errorMessage?: string
}

interface DynamicFormProps {
  fields: FormField[]
  onSubmit: (data: Record<string, string>) => Promise<void>
  submitText?: string
  className?: string
}

export function DynamicForm({
  fields,
  onSubmit,
  submitText = 'Submit',
  className,
}: DynamicFormProps) {
  const [formData, setFormData] = useState<Record<string, string>>({})
  const [errors, setErrors] = useState<Record<string, string>>({})
  const [touched, setTouched] = useState<Record<string, boolean>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [submitError, setSubmitError] = useState<string | null>(null)
  const [submitSuccess, setSubmitSuccess] = useState(false)

  // Initialize form data
  useEffect(() => {
    const initialData: Record<string, string> = {}
    fields.forEach(field => {
      initialData[field.name] = ''
    })
    setFormData(initialData)
  }, [fields])

  const validateField = useCallback((field: FormField, value: string): string | null => {
    if (field.required && !value.trim()) {
      return field.errorMessage || \`\${field.label} is required\`
    }

    if (field.minLength && value.length < field.minLength) {
      return \`\${field.label} must be at least \${field.minLength} characters\`
    }

    if (field.maxLength && value.length > field.maxLength) {
      return \`\${field.label} must be no more than \${field.maxLength} characters\`
    }

    if (field.pattern && !field.pattern.test(value)) {
      return field.errorMessage || \`\${field.label} format is invalid\`
    }

    if (field.type === 'email' && value) {
      const emailRegex = /^[^\\s@]+@[^\\s@]+\\.[^\\s@]+$/
      if (!emailRegex.test(value)) {
        return 'Please enter a valid email address'
      }
    }

    return null
  }, [])

  const handleChange = useCallback((name: string, value: string) => {
    setFormData(prev => ({ ...prev, [name]: value }))

    const field = fields.find(f => f.name === name)
    if (field && touched[name]) {
      const error = validateField(field, value)
      setErrors(prev => ({ ...prev, [name]: error || '' }))
    }
  }, [fields, touched, validateField])

  const handleBlur = useCallback((name: string) => {
    setTouched(prev => ({ ...prev, [name]: true }))

    const field = fields.find(f => f.name === name)
    if (field) {
      const value = formData[name] || ''
      const error = validateField(field, value)
      setErrors(prev => ({ ...prev, [name]: error || '' }))
    }
  }, [fields, formData, validateField])

  const handleSubmit = useCallback(async (e: FormEvent) => {
    e.preventDefault()
    setSubmitError(null)
    setSubmitSuccess(false)

    // Validate all fields
    const newErrors: Record<string, string> = {}
    let hasErrors = false

    fields.forEach(field => {
      const error = validateField(field, formData[field.name] || '')
      if (error) {
        newErrors[field.name] = error
        hasErrors = true
      }
    })

    setErrors(newErrors)
    setTouched(Object.fromEntries(fields.map(f => [f.name, true])))

    if (hasErrors) return

    setIsSubmitting(true)

    try {
      await onSubmit(formData)
      setSubmitSuccess(true)
      // Reset form
      const resetData: Record<string, string> = {}
      fields.forEach(field => {
        resetData[field.name] = ''
      })
      setFormData(resetData)
      setTouched({})
    } catch (err) {
      setSubmitError(err instanceof Error ? err.message : 'Submission failed')
    } finally {
      setIsSubmitting(false)
    }
  }, [fields, formData, onSubmit, validateField])

  return (
    <form onSubmit={handleSubmit} className={className}>
      <AnimatePresence>
        {fields.map((field, index) => (
          <motion.div
            key={field.name}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="mb-4"
          >
            <label className="block text-sm font-medium mb-1">
              {field.label}
              {field.required && <span className="text-red-500 ml-1">*</span>}
            </label>
            {field.type === 'textarea' ? (
              <textarea
                name={field.name}
                value={formData[field.name] || ''}
                onChange={e => handleChange(field.name, e.target.value)}
                onBlur={() => handleBlur(field.name)}
                placeholder={field.placeholder}
                className="w-full px-3 py-2 border rounded-md"
                rows={4}
              />
            ) : (
              <input
                type={field.type}
                name={field.name}
                value={formData[field.name] || ''}
                onChange={e => handleChange(field.name, e.target.value)}
                onBlur={() => handleBlur(field.name)}
                placeholder={field.placeholder}
                className="w-full px-3 py-2 border rounded-md"
              />
            )}
            {touched[field.name] && errors[field.name] && (
              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="text-red-500 text-sm mt-1"
              >
                {errors[field.name]}
              </motion.p>
            )}
          </motion.div>
        ))}
      </AnimatePresence>

      {submitError && (
        <div className="text-red-500 mb-4">{submitError}</div>
      )}

      {submitSuccess && (
        <div className="text-green-500 mb-4">Form submitted successfully!</div>
      )}

      <button
        type="submit"
        disabled={isSubmitting}
        className="w-full py-2 px-4 bg-blue-500 text-white rounded-md"
      >
        {isSubmitting ? 'Submitting...' : submitText}
      </button>
    </form>
  )
}
\`\`\`

