        });
      } else {
        setStart(startDate);
      }
      setDateOpen(false);
    } else if (activeInput === "range-end") {
      // ðŸ”¥ æ—¥åŽ†é€‰æ‹©endæ—¶ä¹Ÿè¦æ£€æŸ¥æŽ¨åŠ¨
      const endDate = newDate as Date | null;
