        <Panel.Row
          triggerRef={rangeRef as unknown as React.RefObject<HTMLDivElement>}
          type="two-input-two-icon"
          className="range-input"
        >
          <DateRangeInput
            locale={localeKey}
            startValue={start}
            endValue={end}
            onStartChange={(newStart) => {
              if (newStart) {
                // è®¡ç®—å½“å‰rangeé•¿åº¦ï¼ˆæ¯«ç§’ï¼‰ï¼Œfallbackä¸º1å¤©ï¼ˆä¸Žåˆå§‹çŠ¶æ€ä¸€è‡´ï¼‰
                const currentRange =
                  start && end
                    ? end.getTime() - start.getTime()
                    : 1 * 24 * 60 * 60 * 1000;
                // ä¿æŒrangeé•¿åº¦
                const newEnd = new Date(newStart.getTime() + currentRange);
                setStart(newStart);
                setEnd(newEnd);
              } else {
                setStart(newStart);
              }
            }}
            onEndChange={(newEnd) => {
              if (newEnd && start && newEnd <= start) {
                // end <= start æ—¶æŽ¨åŠ¨start
                setStart(newEnd);
              }
              setEnd(newEnd);
            }}
            onStartFocus={() => {
              setActiveInput("range-start");
              setDateOpen(true);
            }}
            onEndFocus={() => {
              setActiveInput("range-end");
              setDateOpen(true);
            }}
            onEnterKeyDown={() => {
              setDateOpen(false);
            }}
            startPlaceholder="Start Date"
            endPlaceholder="End Date"
          />
