          variant="accent"
        >
          Accent
        </Checkbox>
        <Checkbox
          value={variant.outline}
          onChange={(value) => setVariant({ ...variant, outline: value })}
          variant="outline"
        >
          Outline
        </Checkbox>
      </div>
    );
  },
};

/**
 * Group: Demonstrates a group of Checkboxes for multi-select scenarios.
 * - Shows how to manage a group of checkboxes with independent state.
 * - Useful for forms, filters, and option lists.
 */

export const Group: Story = {
  render: function GroupStory() {
    const groupOptions = [
      { id: "option1", label: "Option 1" },
      { id: "option2", label: "Option 2" },
      { id: "option3", label: "Option 3" },
    ];

    const [selectedIds, setSelectedIds] = useState<string[]>([]);

    return (
      <div className="flex flex-col gap-2">
        {groupOptions.map((option) => (
          <Checkbox
            key={option.id}
            value={selectedIds.includes(option.id)}
            onChange={(checked) => {
              setSelectedIds((prev) =>
                checked
                  ? [...prev, option.id]
                  : prev.filter((id) => id !== option.id)
              );
            }}
          >
            {option.label}
          </Checkbox>
        ))}
      </div>
    );
  },
};

/**
 * Label Usage: Demonstrates two ways to use labels with Checkbox.
 * - Simple string children: automatically wrapped with Checkbox.Label
 * - Explicit Checkbox.Label: for more complex label content
 */

export const LabelUsage: Story = {
  render: function LabelUsageStory() {
    const [simple, setSimple] = useState(false);
    const [explicit, setExplicit] = useState(false);

    return (
      <div className="flex flex-col gap-4">
        <div>
          <h4 className="font-strong mb-2">
            Simple string label (auto-wrapped):
          </h4>
          <Checkbox value={simple} onChange={setSimple}>
            Simple text label
          </Checkbox>
        </div>

        <div>
          <h4 className="font-strong mb-2">
            Explicit Checkbox.Label (for complex content):
          </h4>
          <Checkbox value={explicit} onChange={setExplicit}>
            <Checkbox.Label>
              <span className="text-accent-foreground">Complex</span> label with{" "}
              <strong>formatting</strong>
            </Checkbox.Label>
          </Checkbox>
        </div>
      </div>
    );
  },
};

/**
 * Checkbox component in readOnly state.
 *
 * In readOnly mode:
 * - The checkbox does not respond to click or keyboard events
 * - The value cannot be changed
 * - Useful for displaying checkbox state without allowing changes
 */

export const Readonly: Story = {
  render: function ReadonlyStory() {
    const [value, setValue] = useState(false);
    const [changeCount, setChangeCount] = useState(0);

    const handleChange = (newValue: boolean) => {
      setValue(newValue);
      setChangeCount((prev) => prev + 1);
    };

    return (
      <div className="flex flex-col gap-4">
        <div className="rounded-xl border bg-stone-50 p-4">
          <div className="text-body-small-strong mb-2 text-stone-700">
            Current Value:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {value ? "true" : "false"}
          </div>
          <div className="text-body-small-strong mt-2 text-stone-700">
            Change Count:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {changeCount}
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <Checkbox readOnly value={value} onChange={handleChange}>
            Readonly Checkbox
          </Checkbox>
          <Checkbox readOnly value={!value} onChange={handleChange}>
            Readonly Checkbox (checked)
          </Checkbox>
          <Checkbox value={value} onChange={handleChange}>
            Normal Checkbox (for comparison)
          </Checkbox>
        </div>

        <div className="text-body-small text-stone-600">
          ðŸ’¡ Try clicking on the readonly checkboxes or pressing Space/Enter -
          the value should not change and the change count should remain at 0.
          Only the normal checkbox will change the value.
        </div>
      </div>
    );
  },
};

export const Basic: Story = {
  render: function BasicStory() {
    return <ChipsInput placeholder="Add tags..." className="w-32" />;
