/**
 * ChipsInput component in readOnly state.
 *
 * In readOnly mode:
 * - The input field is disabled and read-only
 * - Chips cannot be added (Enter key, blur event)
 * - Chips cannot be removed (remove button, Backspace/Delete keys)
 * - Useful for displaying chips without allowing changes
 */

export const Readonly: Story = {
  render: function ReadonlyStory() {
    const [chips, setChips] = useState<string[]>(["apple", "banana", "orange"]);
    const [changeCount, setChangeCount] = useState(0);

    const handleChange = (newChips: string[]) => {
      setChips(newChips);
      setChangeCount((prev) => prev + 1);
    };

    return (
      <div className="flex flex-col gap-4">
        <div className="rounded-xl border bg-stone-50 p-4">
          <div className="text-body-small-strong mb-2 text-stone-700">
            Current Chips:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {chips.length > 0 ? chips.join(", ") : "None"}
          </div>
          <div className="text-body-small-strong mt-2 text-stone-700">
            Change Count:
          </div>
          <div className="text-body-small font-mono text-stone-600">
            {changeCount}
          </div>
        </div>

        <ChipsInput
          readOnly
          value={chips}
          onChange={handleChange}
          placeholder="Add tags..."
          className="w-80"
        />

        <div className="text-body-small text-stone-600">
          ðŸ’¡ Try typing and pressing Enter, clicking chip remove buttons, or
          pressing Backspace/Delete - chips should not change and the change
          count should remain at 0. The input field is read-only.
        </div>
      </div>
    );
  },
};

stories.description:
Basic: Simple context input with user mentions using @ trigger.
- Type @ to search and mention users
- Arrow keys navigation and Enter/Tab to select
- Returns structured data with text and mention metadata
- Real-time search filtering

```tsx
const [value, setValue] = useState({ text: "", mentions: [] })

<ContextInput
  value={value}
  placeholder="Type @ to mention someone..."
  triggers={[{
    char: "@",
    onSearch: async (query) => {
      return users.filter(user =>
        user.label.toLowerCase().includes(query.toLowerCase())
      )
    }
  }]}
  onChange={setValue}
  onMentionSelect={(mention, trigger) => {
    console.log("Selected:", mention, trigger)
  }}
/>
```

