        <ContextInput.Footer>
          <div className="flex items-center gap-2">
            <IconButton
              tooltip={{
                content: "Expand",
              }}
            >
              <AddSmall />
            </IconButton>
            <IconButton
              tooltip={{
                content: "Expand",
              }}
            >
              <Image />
            </IconButton>
          </div>
          <div className="flex items-center gap-2">
            <span className="text-secondary-foreground">Claude Sonnet 4</span>
            <IconButton
