/**
 * FileUpload with progress indicators
 */

export const WithProgress: Story = {
  render: function FileUploadWithProgressDemo() {
    const [files, setFiles] = useState<File[]>([]);

    const simulateUpload = async (
      files: File[],
      {
        onProgress,
        onSuccess,
        onError,
      }: {
        onError: (file: File, error: Error) => void;
        onProgress: (file: File, progress: number) => void;
        onSuccess: (file: File) => void;
      }
    ): Promise<void> => {
      for (const file of files) {
        try {
          // Simulate upload progress
          for (let progress = 0; progress <= 100; progress += 10) {
            await new Promise((resolve) => setTimeout(resolve, 100));
            onProgress(file, progress);
          }
          onSuccess(file);
        } catch (error) {
          onError(
            file,
            error instanceof Error ? error : new Error(String(error))
          );
        }
      }
    };

    return (
      <FileUpload
        maxFiles={3}
        value={files}
        onValueChange={setFiles}
        onUpload={simulateUpload}
        multiple
      >
        <FileUpload.Dropzone>
          <div className="flex flex-col items-center gap-1">
            <DataUpload
              className="text-tertiary-foreground"
              width={32}
              height={32}
              strokeWidth={(2 * 16) / 32}
            />
            <p className="font-strong">Drop files to upload</p>
            <p className="text-secondary-foreground">
              Upload will start automatically
            </p>
          </div>
          <FileUpload.Trigger asChild>
            <Button variant="secondary" className="mt-2 w-fit">
              Select files
            </Button>
          </FileUpload.Trigger>
        </FileUpload.Dropzone>
        <FileUpload.List>
          {files.map((file, index) => (
            <FileUpload.Item key={index} value={file}>
              <FileUpload.ItemPreview />
              <div className="flex-1">
                <FileUpload.ItemMetadata />
                <FileUpload.ItemProgress />
              </div>
              <FileUpload.ItemDelete asChild>
                <IconButton>
                  <RemoveSmall />
                </IconButton>
              </FileUpload.ItemDelete>
            </FileUpload.Item>
          ))}
        </FileUpload.List>
      </FileUpload>
    );
  },
};

