// è‡ªå®šä¹‰é”™è¯¯ç±»åž‹å®šä¹‰
interface ValidationError {
  code?: string;
  details?: Record<string, unknown>;
  field?: string;
  message: string;
  severity?: "error" | "warning" | "info";
  type: "required" | "format" | "length" | "custom" | "server";
}

interface PasswordStrengthError {
  message: string;
  requirements: {
    length: boolean;
    lowercase: boolean;
    numbers: boolean;
    symbols: boolean;
    uppercase: boolean;
  };
  score: number;
  suggestions: string[];
  type: "password_strength";
}

interface FileValidationError {
  file: string;
  issues: Array<{
    current?: string | number;
    limit?: string | number;
    message: string;
    type: "size" | "format" | "name" | "content";
  }>;
  message: string;
  type: "file_validation";
}

// è”åˆé”™è¯¯ç±»åž‹
type CustomError =
  | ValidationError
  | PasswordStrengthError
  | FileValidationError;

/**
 * Form with custom errors
 */

export const WithCustomErrors: Story = {
  render: function WithCustomErrorsRender() {
    const [submitResult, setSubmitResult] = useState<string | null>(null);
    const [serverErrors, setServerErrors] = useState<
      Record<string, ValidationError[]>
    >({});

    // è¾…åŠ©å‡½æ•°ï¼šæ ¼å¼åŒ–ä¸åŒç±»åž‹çš„é”™è¯¯
    const formatCustomErrors = (errors: unknown[]): ReactNode[] => {
      return errors.map((error, index) => {
        // å­—ç¬¦ä¸²é”™è¯¯
        if (typeof error === "string") {
          return (
            <div key={index} className="text-danger-foreground">
              {error}
            </div>
          );
        }

        // éªŒè¯é”™è¯¯å¯¹è±¡
        if (error && typeof error === "object" && "type" in error) {
          const validationError = error as CustomError;

          if (validationError.type === "password_strength") {
            const pwdError = error as PasswordStrengthError;
            return (
              <div key={index} className="space-y-2">
                <div className="text-danger-foreground font-strong">
                  {pwdError.message}
                </div>
                <div className="space-y-1">
                  <div className="flex items-center gap-1">
                    <span className="text-secondary-foreground">
                      Strength score:
                    </span>
                    <div className="flex gap-1">
                      {[1, 2, 3, 4, 5].map((level) => (
                        <div
                          key={level}
                          className={`h-2 w-4 rounded-sm ${
                            level <= pwdError.score
                              ? pwdError.score >= 4
                                ? "bg-success-foreground"
                                : pwdError.score >= 3
                                  ? "bg-warning-foreground"
                                  : "bg-danger-foreground"
                              : "bg-secondary-foreground"
                          }`}
                        />
                      ))}
                    </div>
                    <span className="text-secondary-foreground">
                      ({pwdError.score}/5)
                    </span>
                  </div>
                  <div className="space-y-1">
                    <div className="text-secondary-foreground">
                      Requirements:
                    </div>
                    {Object.entries(pwdError.requirements).map(([key, met]) => (
                      <div key={key} className="flex items-center gap-1">
                        <span
                          className={
                            met
                              ? "text-success-foreground"
                              : "text-danger-foreground"
                          }
                        >
                          {met ? "âœ“" : "âœ—"}
                        </span>
                        <span
                          className={
                            met
                              ? "text-success-foreground"
                              : "text-danger-foreground"
                          }
                        >
                          {key === "length" && "At least 8 characters"}
                          {key === "uppercase" && "Contains uppercase letters"}
                          {key === "lowercase" && "Contains lowercase letters"}
                          {key === "numbers" && "Contains numbers"}
                          {key === "symbols" && "Contains special characters"}
                        </span>
                      </div>
                    ))}
                  </div>
                  {pwdError.suggestions.length > 0 && (
                    <div className="space-y-1">
                      <div className="text-secondary-foreground">
                        Suggestions:
                      </div>
                      {pwdError.suggestions.map((suggestion, i) => (
                        <div key={i} className="text-accent-foreground">
                          â€¢ {suggestion}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            );
          }

          if (validationError.type === "file_validation") {
            const fileError = error as FileValidationError;
            return (
              <div key={index} className="space-y-2">
                <div className="text-danger-foreground font-strong">
                  {fileError.message}
                </div>
                <div className="space-y-1">
                  <div className="text-secondary-foreground">
                    File: {fileError.file}
                  </div>
                  <div className="space-y-1">
                    {fileError.issues.map((issue, i) => (
                      <div key={i} className="flex items-start gap-1">
                        <span className="text-danger-foreground">â€¢</span>
                        <div className="text-danger-foreground">
                          <div>{issue.message}</div>
                          {issue.limit && issue.current && (
                            <div className="text-secondary-foreground mt-1">
                              Current: {issue.current}, Limit: {issue.limit}
                            </div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            );
          }

          // æ™®é€šéªŒè¯é”™è¯¯
          return (
            <div key={index} className="space-y-1">
              <div
                className={` ${
                  validationError.severity === "warning"
                    ? "text-warning-foreground"
                    : validationError.severity === "info"
                      ? "text-accent-foreground"
                      : "text-danger-foreground"
                }`}
              >
                {validationError.message}
              </div>
              {validationError.code && (
                <div className="text-secondary-foreground">
                  Error code: {validationError.code}
                </div>
              )}
              {validationError.details && (
                <div className="text-secondary-foreground">
                  Details: {JSON.stringify(validationError.details)}
                </div>
              )}
            </div>
          );
        }

        // æ•°ç»„é”™è¯¯
        if (Array.isArray(error)) {
          return (
            <div key={index} className="space-y-1">
              {error.map((err, i) => (
                <div key={i} className="text-danger-foreground">
                  â€¢ {typeof err === "string" ? err : JSON.stringify(err)}
                </div>
              ))}
            </div>
          );
        }

        // å…¶ä»–ç±»åž‹é”™è¯¯
        return (
          <div key={index} className="text-danger-foreground">
            {JSON.stringify(error)}
          </div>
        );
      });
    };

    // å¯†ç å¼ºåº¦éªŒè¯å™¨
    const validatePasswordStrength = (
      password: string
    ): PasswordStrengthError | undefined => {
      if (!password) return undefined;

      const requirements = {
        length: password.length >= 8,
        uppercase: /[A-Z]/.test(password),
        lowercase: /[a-z]/.test(password),
        numbers: /\d/.test(password),
        symbols: /[!@#$%^&*(),.?":{}|<>]/.test(password),
      };

      const score = Object.values(requirements).filter(Boolean).length;
      const suggestions: string[] = [];

      if (!requirements.length)
        suggestions.push("Increase password length to at least 8 characters");
      if (!requirements.uppercase) suggestions.push("Add uppercase letters");
      if (!requirements.lowercase) suggestions.push("Add lowercase letters");
      if (!requirements.numbers) suggestions.push("Add numbers");
      if (!requirements.symbols) suggestions.push("Add special characters");

      // å¦‚æžœæ‰€æœ‰è¦æ±‚éƒ½æ»¡è¶³ï¼Œè¿”å›ž undefined (æ— é”™è¯¯)
      if (score === 5) return undefined;

      return {
        type: "password_strength",
        message: "Password strength is not enough",
        requirements,
        score,
        suggestions,
      };
    };

    // æ–‡ä»¶éªŒè¯å™¨
    const validateFile = (
      filename: string
    ): FileValidationError | undefined => {
      if (!filename) return undefined;

      const issues: FileValidationError["issues"] = [];

      // æ£€æŸ¥æ–‡ä»¶æ‰©å±•å
      const allowedExtensions = [
        ".jpg",
        ".jpeg",
        ".png",
        ".pdf",
        ".doc",
        ".docx",
      ];
      const extension = filename
        .toLowerCase()
        .substring(filename.lastIndexOf("."));
      if (!allowedExtensions.includes(extension)) {
        issues.push({
          type: "format",
          message: `Unsupported file format: ${extension}`,
          current: extension,
          limit: allowedExtensions.join(", "),
        });
      }

      // æ£€æŸ¥æ–‡ä»¶åé•¿åº¦
      if (filename.length > 100) {
        issues.push({
          type: "name",
          message: "File name is too long",
          current: filename.length,
          limit: 100,
        });
      }

      // æ¨¡æ‹Ÿæ–‡ä»¶å¤§å°æ£€æŸ¥
      const mockFileSize = filename.length * 1024; // æ¨¡æ‹Ÿæ–‡ä»¶å¤§å°
      if (mockFileSize > 5 * 1024 * 1024) {
        // 5MB
        issues.push({
          type: "size",
          message: "File size exceeds the limit",
          current: `${(mockFileSize / 1024 / 1024).toFixed(1)}MB`,
          limit: "5MB",
        });
      }

      if (issues.length === 0) return undefined;

      return {
        type: "file_validation",
        message: "File validation failed",
        file: filename,
        issues,
      };
    };

    // æ¨¡æ‹ŸæœåŠ¡å™¨éªŒè¯
    const simulateServerValidation = async (
      field: string,
      value: string
    ): Promise<ValidationError | undefined> => {
      // Simulate network delay
      await new Promise((resolve) => setTimeout(resolve, 500));

      if (field === "username" && value === "admin") {
        return {
          type: "server",
          message: "Username is already taken",
          code: "USERNAME_TAKEN",
          field: "username",
          severity: "error",
          details: { suggestions: ["admin123", "admin2024", "user_admin"] },
        };
      }

      if (field === "email" && value === "test@example.com") {
        return {
          type: "server",
          message: "This email is already registered",
          code: "EMAIL_EXISTS",
          field: "email",
          severity: "error",
          details: { registeredAt: "2024-01-01" },
        };
      }

      return undefined;
    };

    // å¤åˆéªŒè¯å™¨ - è¿”å›žå¤šç§é”™è¯¯ç±»åž‹
    const validateComplex = (
      value: string,
      field: string
    ): ValidationError[] | string | undefined => {
      if (!value) {
        return [
          {
            type: "required",
            message: `${field} is required`,
            code: "REQUIRED_FIELD",
            severity: "error",
          } as ValidationError,
        ];
      }

      const errors: ValidationError[] = [];

      // é•¿åº¦æ£€æŸ¥
      if (value.length < 3) {
        errors.push({
          type: "length",
          message: `${field} must be at least 3 characters`,
          code: "MIN_LENGTH",
          severity: "error",
          details: { minLength: 3, currentLength: value.length },
        });
      }

      // æ ¼å¼æ£€æŸ¥
      if (field === "Username" && !/^[a-zA-Z0-9_]+$/.test(value)) {
        errors.push({
          type: "format",
          message: "Username can only contain letters, numbers and underscores",
          code: "INVALID_FORMAT",
          severity: "error",
        });
      }

      // è­¦å‘Šçº§åˆ«çš„æ£€æŸ¥
      if (field === "Username" && value.length > 20) {
        errors.push({
          type: "length",
          message:
            "Username is too long, it is recommended to keep it within 20 characters",
          code: "LONG_USERNAME",
          severity: "warning",
          details: { maxRecommended: 20, currentLength: value.length },
        });
      }

      return errors.length > 0 ? errors : undefined;
    };

    const form = useForm({
      defaultValues: {
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
        fileName: "",
        description: "",
        tags: "",
        score: 0,
        agreement: false,
      },
      onSubmit: async ({ value }) => {
        setSubmitResult(null);
        setServerErrors({});

        try {
          // æ¨¡æ‹ŸæœåŠ¡å™¨ç«¯éªŒè¯
          const serverValidationErrors: Record<string, ValidationError[]> = {};

          // æ£€æŸ¥ç”¨æˆ·å
          const usernameError = await simulateServerValidation(
            "username",
            (value as unknown as { username: string }).username
          );
          if (usernameError) {
            serverValidationErrors.username = [usernameError];
          }

          // æ£€æŸ¥é‚®ç®±
          const emailError = await simulateServerValidation(
            "email",
            (value as unknown as { email: string }).email
          );
          if (emailError) {
            serverValidationErrors.email = [emailError];
          }

          if (Object.keys(serverValidationErrors).length > 0) {
            setServerErrors(serverValidationErrors);
            setSubmitResult(
              "Server validation failed, please check the error information"
            );
            return;
          }

          await new Promise((resolve) => setTimeout(resolve, 1000));
          console.log("Form submitted:", value);
          setSubmitResult("Form submitted successfully!");
        } catch (error) {
          const errorMsg =
            error instanceof Error ? error.message : "Unknown error";
          setSubmitResult(`Submission failed: ${errorMsg}`);
        }
      },
    });

    return (
      <div className="w-80 space-y-4">
        <div className="space-y-2">
          <h3 className="font-strong">Custom error handling example</h3>
          <p className="text-secondary-foreground">
            Demonstrates how to use TanStack Form&apos;s flexible error handling
            mechanism, supporting multiple error types and custom error display
          </p>
        </div>

        <form
          onSubmit={(e) => {
            e.preventDefault();
            form.handleSubmit();
          }}
          className="space-y-6"
        >
          {/* å¤åˆéªŒè¯ç¤ºä¾‹ */}
          <div className="space-y-4 rounded-xl border p-4">
            <h4 className="font-strong">Composite validation example</h4>
            <p className="text-secondary-foreground">
              Demonstrates returning multiple error types: objects, arrays, and
              different severity levels
            </p>

            <div className="grid grid-cols-2 gap-4">
              <form.Field
                name="username"
                validators={{
                  onChange: ({ value }) =>
                    validateComplex(String(value), "Username"),
                  onBlurAsync: async ({ value }) => {
                    if (!value) return undefined;
                    const serverError = await simulateServerValidation(
                      "username",
                      String(value)
                    );
                    return serverError;
                  },
                }}
              >
                {(field) => (
                  <form.Input
                    label="Username"
                    name={field.name}
                    value={String(field.state.value || "")}
                    onChange={field.handleChange}
                    onBlur={field.handleBlur}
                    placeholder="Enter username"
                    error={
                      <>
                        {field.state.meta.errors.length > 0 && (
                          <div className="space-y-1">
                            {formatCustomErrors(field.state.meta.errors)}
                          </div>
                        )}
                        {serverErrors.username && (
                          <div className="space-y-1">
                            {formatCustomErrors(serverErrors.username)}
                          </div>
                        )}
                      </>
                    }
                    description={
                      <>
                        {field.state.meta.isValidating && (
                          <div className="text-accent-foreground">
                            Validating...
                          </div>
                        )}
                      </>
                    }
                  />
                )}
              </form.Field>

              <form.Field
                name="email"
                validators={{
                  onChange: ({ value }) => {
                    const email = String(value || "");
                    if (!email)
                      return {
                        type: "required",
                        message: "Email is required",
                        severity: "error",
                      };
                    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                    if (!emailRegex.test(email)) {
                      return {
                        type: "format",
                        message: "Please enter a valid email address",
                        code: "INVALID_EMAIL_FORMAT",
                        severity: "error",
                        details: { pattern: emailRegex.source },
                      };
                    }
                    return undefined;
                  },
                  onBlurAsync: async ({ value }) => {
                    if (!value) return undefined;
                    return await simulateServerValidation(
                      "email",
                      String(value)
                    );
                  },
                }}
              >
                {(field) => (
                  <form.Input
                    label="Email"
                    name={field.name}
                    value={String(field.state.value || "")}
                    onChange={field.handleChange}
                    onBlur={field.handleBlur}
                    type="email"
                    placeholder="Enter email address"
                    error={
                      <>
                        {field.state.meta.errors.length > 0 && (
                          <div className="space-y-1">
                            {formatCustomErrors(field.state.meta.errors)}
                          </div>
                        )}
                        {serverErrors.email && (
                          <div className="space-y-1">
                            {formatCustomErrors(serverErrors.email)}
                          </div>
                        )}
                      </>
                    }
                    description={
                      <>
                        {field.state.meta.isValidating && (
                          <div className="text-accent-foreground">
                            Validating...
                          </div>
                        )}
                      </>
                    }
                  />
                )}
              </form.Field>
            </div>
          </div>

          {/* å¯†ç å¼ºåº¦éªŒè¯ç¤ºä¾‹ */}
          <div className="space-y-4 rounded-xl border p-4">
            <h4 className="font-strong">
              Password strength validation example
            </h4>
            <p className="text-secondary-foreground">
              Demonstrates complex password strength validation, returning
              detailed error objects
            </p>

            <div className="grid grid-cols-2 gap-4">
              <form.Field
                name="password"
                validators={{
                  onChange: ({ value }) =>
                    validatePasswordStrength(String(value || "")),
                }}
              >
                {(field) => (
                  <form.Input
                    label="Password"
                    name={field.name}
                    value={String(field.state.value || "")}
                    onChange={field.handleChange}
                    onBlur={field.handleBlur}
                    type="password"
                    placeholder="Enter password"
                    error={
                      <>
                        {field.state.meta.errors.length > 0 && (
                          <div className="space-y-1">
                            {formatCustomErrors(field.state.meta.errors)}
                          </div>
                        )}
                      </>
                    }
                  />
                )}
              </form.Field>

              <form.Field
                name="confirmPassword"
                validators={{
                  onChange: ({ value }) => {
                    const password = form.getFieldValue("password");
                    const confirmPassword = String(value || "");

                    if (!confirmPassword) {
                      return {
                        type: "required",
                        message: "Please confirm the password",
                        severity: "error",
                      };
                    }

                    if (password !== confirmPassword) {
                      return {
                        type: "custom",
                        message: "The passwords entered twice do not match",
                        code: "PASSWORD_MISMATCH",
                        severity: "error",
                        details: {
                          originalLength: String(password).length,
                          confirmLength: confirmPassword.length,
                        },
                      };
                    }

                    return undefined;
                  },
                }}
              >
                {(field) => (
                  <form.Input
                    label="Confirm Password"
                    name={field.name}
                    value={String(field.state.value || "")}
                    onChange={field.handleChange}
                    onBlur={field.handleBlur}
                    type="password"
                    placeholder="Enter password again"
                    error={
                      <>
                        {field.state.meta.errors.length > 0 && (
                          <div className="space-y-1">
                            {formatCustomErrors(field.state.meta.errors)}
                          </div>
                        )}
                      </>
                    }
                  />
                )}
              </form.Field>
            </div>
          </div>

          {/* æ–‡ä»¶éªŒè¯ç¤ºä¾‹ */}
          <div className="space-y-4 rounded-xl border p-4">
            <h4 className="font-strong">File validation example</h4>
            <p className="text-secondary-foreground">
              Demonstrates complex file validation logic, including multiple
              validation rules
            </p>

            <form.Field
              name="fileName"
              validators={{
                onChange: ({ value }) => validateFile(String(value || "")),
              }}
            >
              {(field) => (
                <form.Input
                  label="File name"
                  name={field.name}
                  value={String(field.state.value || "")}
                  onChange={field.handleChange}
                  onBlur={field.handleBlur}
                  placeholder="Enter file name (e.g. document.pdf)"
                  error={
                    <>
                      {field.state.meta.errors.length > 0 && (
                        <div className="space-y-1">
                          {formatCustomErrors(field.state.meta.errors)}
                        </div>
                      )}
                    </>
                  }
                  description="Supported formats: .jpg, .jpeg, .png, .pdf, .doc, .docx"
                />
              )}
            </form.Field>
          </div>

          {/* æ•°ç»„é”™è¯¯ç¤ºä¾‹ */}
          <div className="space-y-4 rounded-xl border p-4">
            <h4 className="font-strong">Array error example</h4>
            <p className="text-secondary-foreground">
              Demonstrates returning error arrays, including validation logic
            </p>

            <form.Field
              name="tags"
              validators={{
                onChange: ({ value }) => {
                  const tags = String(value || "");
                  if (!tags) return undefined;

                  const errors: string[] = [];
                  const tagList = tags.split(",").map((tag) => tag.trim());

                  // æ£€æŸ¥æ ‡ç­¾æ•°é‡
                  if (tagList.length > 5) {
                    errors.push("The number of tags cannot exceed 5");
                  }

                  // æ£€æŸ¥æ ‡ç­¾é•¿åº¦
                  const longTags = tagList.filter((tag) => tag.length > 10);
                  if (longTags.length > 0) {
                    errors.push(`Tags are too long: ${longTags.join(", ")}`);
                  }

                  // æ£€æŸ¥é‡å¤æ ‡ç­¾
                  const uniqueTags = new Set(tagList);
                  if (uniqueTags.size !== tagList.length) {
                    errors.push("There are duplicate tags");
                  }

                  // æ£€æŸ¥ç‰¹æ®Šå­—ç¬¦
                  const invalidTags = tagList.filter((tag) =>
                    /[!@#$%^&*(),.?":{}|<>]/.test(tag)
                  );
                  if (invalidTags.length > 0) {
                    errors.push(
                      `Tags contain special characters: ${invalidTags.join(", ")}`
                    );
                  }

                  return errors.length > 0 ? errors : undefined;
                },
              }}
            >
              {(field) => (
                <form.Input
                  label="Tags"
                  name={field.name}
                  value={String(field.state.value || "")}
                  onChange={field.handleChange}
                  onBlur={field.handleBlur}
                  placeholder="Enter tags, separated by commas"
                  error={
                    <>
                      {field.state.meta.errors.length > 0 && (
                        <div className="space-y-1">
                          {formatCustomErrors(field.state.meta.errors)}
                        </div>
                      )}
                    </>
                  }
                  description="Separate multiple tags with commas, up to 5, each tag is no more than 10 characters"
                />
              )}
            </form.Field>
          </div>

          {/* æ•°å€¼éªŒè¯ç¤ºä¾‹ */}
          <div className="space-y-4 rounded-xl border p-4">
            <h4 className="font-strong">Number validation example</h4>
            <p className="text-secondary-foreground">
              Demonstrates returning numeric or boolean values as errors
            </p>

            <form.Field
              name="score"
              validators={{
                onChange: ({ value }) => {
                  const score = Number(value);

                  // è¿”å›žå¸ƒå°”å€¼ - true è¡¨ç¤ºé”™è¯¯
                  if (isNaN(score)) return true;

                  // è¿”å›žæ•°å­— - éžé›¶è¡¨ç¤ºé”™è¯¯
                  if (score < 0) return -1;
                  if (score > 100) return 1;

                  // è¿”å›ž undefined è¡¨ç¤ºæ— é”™è¯¯
                  return undefined;
                },
              }}
            >
              {(field) => (
                <form.NumericInput
                  label="Score"
                  name={field.name}
                  value={Number(field.state.value || "")}
                  onChange={field.handleChange}
                  onBlur={field.handleBlur}
                  placeholder="Enter score (0-100)"
                  error={
                    field.state.meta.errors.length > 0 && (
                      <div className="space-y-1">
                        {field.state.meta.errors.map((error, index) => (
                          <div key={index}>
                            {error === true && "Please enter a valid number"}
                            {error === -1 && "Score cannot be negative"}
                            {error === 1 && "Score cannot exceed 100"}
                            {typeof error === "string" && error}
                          </div>
                        ))}
                      </div>
                    )
                  }
                />
              )}
            </form.Field>
          </div>

          {/* æè¿°å­—æ®µ */}
          <form.Field
            name="description"
            validators={{
              onChange: ({ value }) => {
                const desc = String(value || "");
                if (desc.length > 200) {
                  return {
                    type: "length",
                    message: "Description cannot exceed 200 characters",
                    severity: "warning",
                    details: { maxLength: 200, currentLength: desc.length },
                  };
                }
                return undefined;
              },
            }}
          >
            {(field) => (
              <form.Textarea
                label="Description"
                name={field.name}
                value={String(field.state.value || "")}
                onChange={(e) => field.handleChange(e)}
                onBlur={field.handleBlur}
                rows={4}
                placeholder="Enter description"
                error={
                  field.state.meta.errors.length > 0 && (
                    <div className="space-y-1">
                      {formatCustomErrors(field.state.meta.errors)}
                    </div>
                  )
                }
                description={`Current characters: ${String(field.state.value || "").length}/200`}
              />
            )}
          </form.Field>

          {/* åè®®åŒæ„ */}
          <form.Field
            name="agreement"
            validators={{
              onChange: ({ value }) => {
                if (!value) {
                  return {
                    type: "required",
                    message: "Please agree to the user agreement",
                    severity: "error",
                  };
                }
                return undefined;
              },
            }}
          >
            {(field) => (
              <form.Checkbox
                label="I have read and agree to the user agreement"
                name={field.name}
                value={Boolean(field.state.value)}
                onChange={(e) => field.handleChange(e)}
                onBlur={field.handleBlur}
                error={
                  field.state.meta.errors.length > 0 && (
                    <div className="space-y-1">
                      {formatCustomErrors(field.state.meta.errors)}
                    </div>
                  )
                }
              />
            )}
          </form.Field>

          {/* æäº¤æŒ‰é’® */}
          <div className="flex gap-2">
            <form.Button
              type="submit"
              disabled={!form.state.canSubmit || form.state.isSubmitting}
            >
              {form.state.isSubmitting ? "Submitting..." : "Submit form"}
            </form.Button>

            <form.Button
              type="button"
              variant="secondary"
              onClick={() => {
                form.reset();
                setServerErrors({});
              }}
            >
              Reset form
            </form.Button>
          </div>
        </form>

        {submitResult && (
          <div
            className={tcx(
              "rounded-xl p-4",
              submitResult.includes("success")
                ? "text-success-foreground bg-green-100"
                : "text-danger-foreground bg-red-100"
            )}
          >
            {submitResult}
          </div>
        )}

        {/* é”™è¯¯ç±»åž‹è¯´æ˜Ž */}
        <div className="rounded-xl border p-4">
          <div className="font-strong mb-2">Supported error types:</div>
          <div className="text-secondary-foreground space-y-1">
            <div>
              â€¢ <strong>String</strong>: The most common error type
            </div>
            <div>
              â€¢ <strong>Object</strong>: Contains type, message, severity, and
              other detailed information
            </div>
            <div>
              â€¢ <strong>Array</strong>: A collection of multiple errors
            </div>
            <div>
              â€¢ <strong>Boolean</strong>: true means error, false means correct
            </div>
            <div>
              â€¢ <strong>Number</strong>: Non-zero values mean error
            </div>
            <div>
              â€¢ <strong>undefined/null</strong>: Means no error
            </div>
          </div>
        </div>
      </div>
    );
  },
};

export const Disabled: Story = {
  render: function DisabledStory() {
    const [value, setValue] = useState("Disabled");
    return (
      <Input value={value} onChange={(value) => setValue(value)} disabled />
    );
  },
};

/**
 * ReadOnly: Demonstrates the input in a read-only state.
 *
 * Features:
 * - Prevents editing while still allowing focus and selection
 * - Maintains normal visual appearance (unlike disabled)
 * - Value is included in form submission
 *
 * Use read-only inputs when:
 * - You want to display a value that shouldn't be edited
 * - The value is calculated or provided by the system
 * - You need the field to remain focusable (unlike disabled)
 */

export const ReadOnly: Story = {
  render: function ReadOnlyStory() {
    const [value, setValue] = useState("Read Only");
    return (
      <Input readOnly value={value} onChange={(value) => setValue(value)} />
    );
  },
};

/**
 * Selected: Demonstrates the input in a selected state.
 *
 * Features:
 * - Visual indication that the input is currently selected
 * - Useful for highlighting the active input in a form
 * - Remains editable while showing selection state
 *
 * Use selected inputs when:
 * - You want to highlight which field is currently relevant
 * - The parent container or context is selected
 * - You need to draw attention to a specific field
 */

export const Selected: Story = {
  render: function SelectedStory() {
    const [value, setValue] = useState("Selected");
    return (
      <Input value={value} onChange={(value) => setValue(value)} selected />
    );
  },
};

/**
 * Placeholder: Demonstrates using placeholder text in the input.
 *
 * Features:
 * - Provides hint text when the input is empty
 * - Disappears when the user enters text
 * - Helps users understand the expected content
 *
 * Best Practices:
 * - Use concise, descriptive placeholder text
 * - Don't rely on placeholders for critical information
 * - Consider placeholder text as supplementary to labels
 * - Ensure sufficient contrast for readability
 */

export const Placeholder: Story = {
  render: function PlaceholderStory() {
    const [value, setValue] = useState("");
    return (
      <Input
        placeholder="Placeholder"
        value={value}
        onChange={(value) => setValue(value)}
      />
    );
  },
};

/**
 * Variants: Demonstrates different visual variants of the input component.
 * Features:
 * - default: Follows the page theme dynamically (light/dark mode)
 * - light: Fixed light appearance regardless of theme
 * - dark: Fixed dark appearance regardless of theme
 * - reset: Removes variant styling, no Variant settings applied
 *
 * Usage:
 * - Use default variant for inputs that adapt to the current theme
 * - Use light/dark variants when you need fixed appearance
 * - Use reset variant to remove all variant-specific styling
 */

stories.description:
Controlled: Demonstrates using the Input component in controlled mode.

Features:
- Input value managed by React state
- Complete control over value changes
- Enables validation, formatting, or transformation during input

Usage:
```tsx
const [value, setValue] = useState("");

// With validation
const handleChange = (newValue) => {
  // Apply validation or formatting logic
  setValue(newValue);
};

return <Input value={value} onChange={handleChange} />;
```

Best Practices:
- Use controlled inputs when you need to:
  - Validate input on change
  - Format values as they're entered
  - Synchronize with other components
  - Implement complex form logic

