        id: "1",
        label: "John Doe",
        email: "john@example.com",
        avatar: `https://api.dicebear.com/7.x/avataaars/svg?seed=1`,
      },
      {
        id: "2",
        label: "Jane Smith",
        email: "jane@example.com",
        avatar: `https://api.dicebear.com/7.x/avataaars/svg?seed=2`,
