// Helper component that integrates ScrollArea with react-virtual
function VirtualScrollArea({
  items,
}: {
  items: Array<{
    description: string;
    id: number;
    name: string;
    value: number;
  }>;
}) {
  const [scrollElement, setScrollElement] =
    React.useState<HTMLDivElement | null>(null);

  const virtualizer = useVirtualizer({
    count: items.length,
    getScrollElement: () => scrollElement,
    estimateSize: () => 60,
    overscan: 5,
  });

  return (
    <ScrollArea
      className="relative h-96 w-80 overflow-hidden rounded-xl border border-gray-200"
      type="hover"
      scrollbarMode="large-y"
    >
      <ScrollArea.Viewport className="h-full" ref={setScrollElement}>
        <ScrollArea.Content>
          <div
            style={{
              height: virtualizer.getTotalSize(),
              position: "relative",
            }}
          >
            {virtualizer.getVirtualItems().map((virtualItem) => {
              const item = items[virtualItem.index];
              return (
                <div
                  key={virtualItem.key}
                  className="absolute top-0 left-0 w-full border-b border-gray-100 p-3 transition-colors hover:bg-gray-50"
                  style={{
                    height: virtualItem.size,
                    transform: `translateY(${virtualItem.start}px)`,
                  }}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <div className="font-strong text-gray-900">
                        {item.name}
                      </div>
                      <div className="text-body-small text-gray-500">
                        {item.description}
                      </div>
                    </div>
                    <div className="text-body-small rounded bg-gray-100 px-2 py-1 font-mono text-gray-600">
                      {item.value}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </ScrollArea.Content>
      </ScrollArea.Viewport>
      <ScrollArea.Scrollbar orientation="vertical">
        <ScrollArea.Thumb orientation="vertical" />
      </ScrollArea.Scrollbar>
    </ScrollArea>
  );
}

