/**
 * ControlledFixed: Demonstrates a controlled popover with fixed position.
 *
 * Features:
 * - Fixed position
 * - Controlled open state
 * - Toggle count
 * - Force open/close
 *
 * This pattern is useful for:
 * - Popovers that need to be in a specific location
 * - Dashboard interfaces where popovers need to be in a specific location
 * - Popovers that need to be in a specific location
 *
 * Note: This pattern is based on the correct implementation in the Floating UI official
 * documentation.
 */

export const ControlledFixed: Story = {
  render: function ControlledFixedStory() {
    const [open, setOpen] = useState(false);
    const [toggleCount, setToggleCount] = useState(0);

    const handleToggle = () => {
      setOpen(!open);
      setToggleCount((prev) => prev + 1);
    };

    const handleOpenChange = (isOpen: boolean) => {
      setOpen(isOpen);
      if (isOpen) {
        setToggleCount((prev) => prev + 1);
      }
    };

    return (
      <div className="w-md space-y-6">
        <div className="rounded-xl border p-4">
          <h3 className="font-strong mb-2">Controlled</h3>
          <p>
            Based on the correct implementation in the Floating UI official
            documentation, it solves the problem of not being able to display
            again after closing once
          </p>
        </div>

        <div className="rounded-xl border border-gray-200 bg-gray-50 p-4">
          <div className="mb-4 space-y-2">
            <div className="font-strong">
              Current state:
              <span
                className={tcx(
                  open ? "text-success-foreground" : "text-danger-foreground"
                )}
              >
                {open ? " OPEN" : " CLOSED"}
              </span>
            </div>
            <p>
              Toggle count: <span className="font-mono">{toggleCount}</span>
            </p>
          </div>

          <div className="flex gap-2">
            <Button
              onClick={handleToggle}
              variant={open ? "destructive" : "success"}
            >
              {open ? "Close" : "Open"} Popover
            </Button>

            <Button
              onClick={() => setOpen(true)}
              disabled={open}
              variant="success"
            >
              Force open
            </Button>

            <Button
              onClick={() => setOpen(false)}
              disabled={!open}
              variant="destructive"
            >
              Force close
            </Button>
          </div>
        </div>

        <Popover open={open} onOpenChange={handleOpenChange}>
          <Popover.Trigger>
            <Button>Popover trigger</Button>
          </Popover.Trigger>
          <Popover.Content className="w-80 p-4">
            <div className="space-y-3">
              <h4 className="font-strong">Controlled</h4>
              <p className="text-secondary-foreground">
                This Popover uses controlled mode, based on the correct
