export const Basic: Story = {
  render: function BasicStory() {
    const items = useMemo(() => generateItems(1000), []);

    return (
      <VirtualizedGrid
        items={items}
        overscan={5}
        columnCount={(width, gap) =>
          Math.max(1, Math.floor((width + gap) / (250 + gap)))
        }
        gridGap={(width) => (width > 768 ? 24 : 16)}
        itemData={(item) => ({
          key: item.id,
          height: 280,
