import { IconButton } from "@choice-ui/icon-button"
import { useState } from "react"

export default function App() {

  enum Variant {
    Dark = "dark",
    Default = "default",
    Ghost = "ghost",
    Highlight = "highlight",
    Secondary = "secondary",
    Solid = "solid",
  }

  enum State {
    Active = "active",
    Disabled = "disabled",
    Focused = "focused",
    Loading = "loading",
    Rest = "rest",
  }

  const [variant, setVariant] = useState<Variant>(Variant.Default)

  return (
    <div
      className={tcx(
        "flex flex-col items-start gap-4 rounded-xl p-4",
        variant === Variant.Dark && "bg-gray-800",
      )}
    >
      <Select
        value={variant}
        onChange={(value) => setVariant(value as Variant)}
      >
        <Select.Trigger>
          {Object.keys(Variant).find((v) => Variant[v as keyof typeof Variant] === variant)}
        </Select.Trigger>
        <Select.Content>
          {Object.entries(Variant).map(([key, value]) => (
            <Select.Item
              key={key}
              value={value}
            >
              {key}
            </Select.Item>
          ))}
        </Select.Content>
      </Select>

      <div className="grid grid-cols-[auto_1fr] items-center gap-4">
        {Object.values(State).map((state) => (
          <Fragment key={state}>
            <span className="text-pink-500 capitalize">{state}</span>
            <IconButton
              variant={variant}
              active={state === State.Active}
              disabled={state === State.Disabled}
              loading={state === State.Loading}
              focused={state === State.Focused}
            >
              <FieldTypeRating />
            </IconButton>
          </Fragment>
        ))}
      </div>
    </div>
  )

}

