import { LinkButton } from "@choice-ui/link-button"

export default function App() {
  return (
    <LinkButton onClick={() => alert("Button clicked!")}>Click me</LinkButton>
  );
}

import { LinkButton } from "@choice-ui/link-button"

export default function App() {
  return (
    <LinkButton href="/dashboard">Go to Dashboard</LinkButton>
  );
}

import { LinkButton } from "@choice-ui/link-button"

export default function App() {
  return (
    <LinkButton href="https://github.com">Visit GitHub</LinkButton>
  );
}

import { LinkButton } from "@choice-ui/link-button"

export default function App() {
  return (

    <div className="flex gap-4">
      <LinkButton
        disabled
        href="/example"
      >
        Disabled Link
      </LinkButton>
      <LinkButton
        disabled
        onClick={() => alert("Won't fire")}
      >
        Disabled Button
      </LinkButton>
    </div>

  );
}

import { LinkButton } from "@choice-ui/link-button"

export default function App() {
  return (

    <div className="flex gap-4">
      <LinkButton
        href="/home"
        className="gap-1"
      >
        <Settings
          width={16}
          height={16}
        />
        Home
      </LinkButton>
      <LinkButton
        href="/settings"
        variant="subtle"
        className="gap-1"
      >
        <Settings
          width={16}
          height={16}
        />
        Settings
      </LinkButton>
    </div>

  );
}
