import { Segmented } from "@choice-ui/segmented"

export default function App() {
  return (
    <Segmented value={value} onChange={(value) => setValue(value)}>
      <Segmented.Item
        value="sun"
        tooltip={{
          content: "Sun",
        }}
      >
        <FieldTypeAttachment />
      </Segmented.Item>
      <Segmented.Item
        value="moon"
        tooltip={{
          content: "Moon",
        }}
      >
        <FieldTypeCheckbox />
      </Segmented.Item>
      <Segmented.Item
        value="system"
        tooltip={{
          content: "System",
        }}
      >
        <FieldTypeCount />
      </Segmented.Item>
    </Segmented>
  );
}
