import { ToggleButton } from "@choice-ui/toggle-button"

export default function App() {
  return (
    <ToggleButton
      aria-label="Toggle button"
      value={controlled}
      onChange={(value) => setControlled(value)}
    >
      {controlled ? <FieldTypeCheckbox /> : <FieldAdd />}
    </ToggleButton>
  );
}

import { ToggleButton } from "@choice-ui/toggle-button"

export default function App() {
  return (
    <div className="flex flex-col items-start gap-4">
      <div className="flex gap-4">
        <Select
          value={variant}
          onChange={(value) => setVariant(value as Variant)}
        >
          <Select.Trigger>{variant}</Select.Trigger>
          <Select.Content>
            {Object.values(Variant).map((variant) => (
              <Select.Item
                key={variant}
                value={variant}
              >
                {variant}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
        <Select
          value={size}
          onChange={(value) => setSize(value as Size)}
        >
          <Select.Trigger>{size}</Select.Trigger>
          <Select.Content>
            {Object.values(Size).map((size) => (
              <Select.Item
                key={size}
                value={size}
              >
                {size}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
        <Select
          value={event}
          onChange={(value) => setEvent(value as Event)}
        >
          <Select.Trigger>{event}</Select.Trigger>
          <Select.Content>
            {Object.values(Event).map((event) => (
              <Select.Item
                key={event}
                value={event}
              >
                {event}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
      </div>

      <div className="grid grid-cols-[auto_1fr] items-center gap-4">
        {Object.values(State).map((state) => (
          <Fragment key={state}>
            <span className="text-fuchsia-500 capitalize">{state}</span>
            <ToggleButton
              variant={variant}
              value={controlled}
              onChange={(value) => setControlled(value)}
              size={size}
              active={state === State.Active}
              focused={state === State.Focused}
              disabled={state === State.Disabled}
              event={event}
            >
              {controlled ? <FieldTypeCheckbox /> : <FieldTypeAttachment />}
            </ToggleButton>
          </Fragment>
        ))}
      </div>
    </div>
  );
}

import { ToggleButton } from "@choice-ui/toggle-button"

export default function App() {
  return (
    <ToggleButton
      tooltip={{
        content: controlled ? "Current state is correct" : "Current state is wrong",
      }}
      value={controlled}
      onChange={(value) => setControlled(value)}
    >
      {controlled ? <FieldTypeCheckbox /> : <FieldAdd />}
    </ToggleButton>
  );
}

import { ToggleButton } from "@choice-ui/toggle-button"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <div className="rounded-xl border bg-stone-50 p-4">
        <div className="text-body-small-strong mb-2 text-stone-700">Current Value:</div>
        <div className="text-body-small font-mono text-stone-600">{value ? "true" : "false"}</div>
        <div className="text-body-small-strong mt-2 text-stone-700">Change Count:</div>
        <div className="text-body-small font-mono text-stone-600">{changeCount}</div>
      </div>

      <div className="flex flex-wrap gap-4">
        <ToggleButton
          readOnly
          value={value}
          onChange={handleChange}
        >
          {value ? <FieldTypeCheckbox /> : <FieldAdd />}
        </ToggleButton>
        <ToggleButton
          readOnly
          value={!value}
          onChange={handleChange}
          variant="secondary"
        >
          {!value ? <FieldTypeCheckbox /> : <FieldAdd />}
        </ToggleButton>
        <ToggleButton
          value={value}
          onChange={handleChange}
        >
          {value ? <FieldTypeCheckbox /> : <FieldAdd />}
        </ToggleButton>
      </div>

      <div className="text-body-small text-stone-600">
        ðŸ’¡ Try clicking on the readonly toggle buttons - the value should not change and the
        change count should remain at 0. Only the normal button will change the value.
      </div>
    </div>
  );
}
