import { Component } from "@choice-ui/dropdown"

export default function App() {
  return (
    <Dropdown selection={true}>
      <Dropdown.Trigger>
        <Dropdown.Value>Theme: {selected || "None"}</Dropdown.Value>
      </Dropdown.Trigger>
      <Dropdown.Content>
        <Dropdown.Label>Select Theme</Dropdown.Label>
        {options.map((option) => (
          <Dropdown.Item
            key={option}
            selected={selected === option}
            onClick={() => setSelected(option)}
          >
            <Dropdown.Value>{option}</Dropdown.Value>
          </Dropdown.Item>
        ))}
      </Dropdown.Content>
    </Dropdown>
  );
}

import { Component } from "@choice-ui/dropdown"

export default function App() {
  return (
    <Dropdown selection={true}>
      <Dropdown.Trigger>
        <Dropdown.Value>Multi-Level Selection</Dropdown.Value>
      </Dropdown.Trigger>
      <Dropdown.Content>
        <Dropdown.Label>Main Options</Dropdown.Label>
        {["Main 1", "Main 2"].map((option) => (
          <Dropdown.Item
            key={option}
            selected={selected === option}
            onPointerUp={() => setSelected(option)}
          >
            <Dropdown.Value>{option}</Dropdown.Value>
          </Dropdown.Item>
        ))}
        <Dropdown.Divider />
        <Dropdown selection={true}>
          <Dropdown.SubTrigger
            selected={selected === "Sub Options"}
            onPointerUp={() => setSelected("Sub Options")}
          >
            <Dropdown.Value>Sub Options</Dropdown.Value>
          </Dropdown.SubTrigger>
          <Dropdown.Content>
            <Dropdown.Label>Choose Sub Option</Dropdown.Label>
            {["Sub A", "Sub B", "Sub C"].map((option) => (
              <Dropdown.Item
                key={option}
                selected={selected === option}
                onClick={() => setSelected(option)}
              >
                <Dropdown.Value>{option}</Dropdown.Value>
              </Dropdown.Item>
            ))}
          </Dropdown.Content>
        </Dropdown>
      </Dropdown.Content>
    </Dropdown>
  );
}

import { Component } from "@choice-ui/dropdown"

export default function App() {
  return (
    <Dropdown>
      <Dropdown.Trigger>
        <Dropdown.Value>Editor Menu</Dropdown.Value>
      </Dropdown.Trigger>
      <Dropdown.Content>
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "N",
          }}
        >
          <Dropdown.Value>New File</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "O",
          }}
        >
          <Dropdown.Value>Open File</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "S",
          }}
        >
          <Dropdown.Value>Save</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Divider />
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "Z",
          }}
        >
          <Dropdown.Value>Undo</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Item
          shortcut={{
            modifier: "shift",
            keys: "Command+Z",
          }}
        >
          <Dropdown.Value>Redo</Dropdown.Value>
        </Dropdown.Item>
      </Dropdown.Content>
    </Dropdown>
  );
}

import { Component } from "@choice-ui/dropdown"

export default function App() {
  return (
    <Dropdown selection={true} open={isOpen} onOpenChange={setIsOpen}>
      <Dropdown.Trigger>
        <Dropdown.Value>
          Search Music ({selected.length} selected)
        </Dropdown.Value>
      </Dropdown.Trigger>
      <Dropdown.Content>
        <Dropdown.Search
          value={search}
          onChange={setSearch}
          placeholder="Search genres..."
        />
        <Dropdown.Divider />

        {filteredOptions.length > 0 ? (
          <>
            <Dropdown.Label>Music Genres</Dropdown.Label>
            {filteredOptions.map((option) => (
              <Dropdown.Item
                key={option.id}
                selected={selected.includes(option.id)}
                onClick={() => {
                  setSelected((prev) =>
                    prev.includes(option.id)
                      ? prev.filter((id) => id !== option.id)
                      : [...prev, option.id]
                  );
                }}
              >
                <Dropdown.Value>{option.label}</Dropdown.Value>
              </Dropdown.Item>
            ))}
            {selected.length > 0 && (
              <>
                <Dropdown.Divider />
                <Dropdown.Button onClick={() => setSelected([])}>
                  Clear All Selections
                </Dropdown.Button>
              </>
            )}
          </>
        ) : (
          <div className="flex h-32 flex-col items-center justify-center gap-2 text-center text-white/40">
            <Search width={24} height={24} />
            <span>No results found</span>
            <Button variant="link" onClick={() => setSearch("")}>
              Clear Search
            </Button>
          </div>
        )}
      </Dropdown.Content>
    </Dropdown>
  );
}

import { Component } from "@choice-ui/dropdown"

export default function App() {
  return (
    <Dropdown>
      <Dropdown.Trigger>
        <Dropdown.Value>Long List (100 items)</Dropdown.Value>
      </Dropdown.Trigger>
      <Dropdown.Content>
        <Dropdown.Label>Countries</Dropdown.Label>
        {Array.from({ length: 100 }, (_, i) => (
          <Dropdown.Item key={i}>
            <Dropdown.Value>
              {faker.location.country()} {i + 1}
            </Dropdown.Value>
          </Dropdown.Item>
        ))}
      </Dropdown.Content>
    </Dropdown>
  );
}

