import { Dropdown } from "@choice-ui/dropdown"

export default function App() {

  return (
    <Dropdown>
      <Dropdown.Trigger>
        <Dropdown.Value>Editor Menu</Dropdown.Value>
      </Dropdown.Trigger>
      <Dropdown.Content>
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "N",
          }}
        >
          <Dropdown.Value>New File</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "O",
          }}
        >
          <Dropdown.Value>Open File</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "S",
          }}
        >
          <Dropdown.Value>Save</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Divider />
        <Dropdown.Item
          shortcut={{
            modifier: "command",
            keys: "Z",
          }}
        >
          <Dropdown.Value>Undo</Dropdown.Value>
        </Dropdown.Item>
        <Dropdown.Item
          shortcut={{
            modifier: ["shift", "command"],
            keys: "Z",
          }}
        >
          <Dropdown.Value>Redo</Dropdown.Value>
        </Dropdown.Item>
      </Dropdown.Content>
    </Dropdown>
  )

}

