        >
          <span className="flex-1 truncate">{option.label}</span>
        </Menus.Item>
      ))}
    </Menus>
  );
}

import { Component } from "@choice-ui/menus"

export default function App() {
  return (
    <Menus>
      {Array.from({ length: 4 }).map((_, index) => (
        <Menus.Item
          key={index}
          active={activeIndex === index}
          onMouseEnter={() => setActiveIndex(index)}
          onMouseLeave={() => setActiveIndex(null)}
          prefixElement={selectedIndex === index ? <Check /> : <></>}
          onMouseDown={() => setSelectedIndex(index)}
        >
          <span className="flex-1 truncate">Item</span>
        </Menus.Item>
      ))}
    </Menus>
  );
}

import { Component } from "@choice-ui/menus"

export default function App() {
  return (
    <Menus>
      {Array.from({ length: 4 }).map((_, index) => (
        <Menus.Item
          key={index}
          active={activeIndex === index}
          onMouseEnter={() => setActiveIndex(index)}
          onMouseLeave={() => setActiveIndex(null)}
          suffixElement={
            <Menus.Checkbox
              active={activeIndex === index}
              selected={selectedIndex.includes(index)}
            />
          }
          onMouseDown={() =>
            setSelectedIndex((prev) =>
              prev.includes(index)
                ? prev.filter((i) => i !== index)
                : [...prev, index]
            )
          }
        >
          <span className="flex-1 truncate">Item</span>
        </Menus.Item>
      ))}
    </Menus>
  );
}

import { Component } from "@choice-ui/menus"

export default function App() {
  return (
    <Menus>
      {Options.map((option, index) => (
        <Menus.Item
          key={option.name}
          active={activeIndex === index}
          onMouseEnter={() => setActiveIndex(index)}
          onMouseLeave={() => setActiveIndex(null)}
        >
          <Avatar
            size="small"
            color={option.color}
            photo={option.picture}
            name={option.name}
          />
          <span className="flex-1 truncate">{option.name}</span>
        </Menus.Item>
      ))}
    </Menus>
  );
}

import { Component } from "@choice-ui/menus"

export default function App() {
  return (
    <Menus>
      {Options.map((option, index) => (
        <Menus.Item
          key={index}
          active={activeIndex === index}
          onMouseEnter={() => setActiveIndex(index)}
          onMouseLeave={() => setActiveIndex(null)}
          onMouseDown={() =>
            setSelectedIndex((prev) =>
              prev.includes(index)
                ? prev.filter((i) => i !== index)
                : [...prev, index]
            )
          }
          prefixElement={selectedIndex.includes(index) ? <Check /> : <></>}
          suffixElement={
            <div
              className={tcx(
                "mr-1 ml-2 flex items-center justify-center rounded-md border px-1",
                activeIndex === index && "bg-menu-background",
                selectedIndex.includes(index) && activeIndex !== index
                  ? "border-white/20"
                  : "border-transparent"
              )}
            >
              Badge
            </div>
          }
        >
          <span className="flex-1 truncate">{option.label}</span>
        </Menus.Item>
      ))}
    </Menus>
  );
}

import { Component } from "@choice-ui/menus"

export default function App() {
  return (
    <Menus>
      <Menus.Item
        variant="highlight"
        active={activeIndex === 0}
        onMouseEnter={() => setActiveIndex(0)}
        onMouseLeave={() => setActiveIndex(null)}
      >
        Highlight
      </Menus.Item>
      <Menus.Item
        variant="danger"
        active={activeIndex === 1}
        onMouseEnter={() => setActiveIndex(1)}
        onMouseLeave={() => setActiveIndex(null)}
      >
        Danger
      </Menus.Item>
    </Menus>
  );
}
