import { MultiSelect } from "@choice-ui/multi-select"
import { useState, useCallback, useMemo } from "react"

export default function App() {

  const [values, setValues] = useState<string[]>(["option-2", "option-4"])

  const options = useMemo(
    () =>
      Array.from({ length: 8 }, (_, i) => ({
        value: `option-${i + 1}`,
        label: `Option ${i + 1}`,
      })),
    [],
  )

  const getDisplayValue = useCallback(
    (value: string) => {
      return options.find((opt) => opt.value === value)?.label || value
    },
    [options],
  )

  return (
    <div className="space-y-4">
      <div className="flex flex-col gap-2">
        <Label>Basic Multi-Select</Label>
        <MultiSelect
          values={values}
          onChange={setValues}
        >
          <MultiSelect.Trigger
            placeholder="Select options..."
            getDisplayValue={getDisplayValue}
            className="w-80"
          />
          <MultiSelect.Content>
            <MultiSelect.Label>Available Options</MultiSelect.Label>
            {options.map((option) => (
              <MultiSelect.Item
                key={option.value}
                value={option.value}
              >
                {option.label}
              </MultiSelect.Item>
            ))}
          </MultiSelect.Content>
        </MultiSelect>
      </div>

      <div className="w-80 rounded-xl border p-4">
        <h3 className="font-strong mb-2">Selected Values</h3>
        <div className="space-y-1">
          <div>
            Count: <span className="font-mono">{values.length}</span>
          </div>
          <div>
            Values: <span className="font-mono">[{values.join(", ")}]</span>
          </div>
        </div>
      </div>
    </div>
  )

}

