import { Component } from "@choice-ui/multi-select"

export default function App() {
  return (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="font-strong">
          Multi-Select with Limits (Max 3, Min 1)
        </label>
        <MultiSelect
          values={values}
          onChange={setValues}
          maxSelection={3}
          minSelection={1}
        >
          <MultiSelect.Trigger
            placeholder="Select 1-3 options..."
            getDisplayValue={getDisplayValue}
            className="w-80"
          />
          <MultiSelect.Content>
            <MultiSelect.Label>Available Options</MultiSelect.Label>
            {options.map((option) => (
              <MultiSelect.Item key={option.value} value={option.value}>
                {option.label}
              </MultiSelect.Item>
            ))}
          </MultiSelect.Content>
        </MultiSelect>
      </div>

      <div className="w-80 rounded-xl border p-4">
        <h3 className="font-strong mb-2">Selection Status</h3>
        <div className="space-y-1">
          <div>
            Count: <span className="font-mono">{values.length}</span>
          </div>
          <div>
            Can add more:{" "}
            <span className="font-mono">
              {values.length < 3 ? "Yes" : "No"}
            </span>
          </div>
          <div>
            Can remove:{" "}
            <span className="font-mono">
              {values.length > 1 ? "Yes" : "No"}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

import { Component } from "@choice-ui/multi-select"

export default function App() {
  return (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="font-strong">Field Types Selection</label>
        <MultiSelect values={values} onChange={setValues} maxSelection={3}>
          <MultiSelect.Trigger
            placeholder="Select field types..."
            getDisplayValue={getDisplayValue}
            className="w-80"
          />
          <MultiSelect.Content>
            <MultiSelect.Label>Field Types</MultiSelect.Label>
            {options.map((option) => (
              <MultiSelect.Item key={option.value} value={option.value}>
                {option.icon}
                {option.label}
              </MultiSelect.Item>
            ))}
          </MultiSelect.Content>
        </MultiSelect>
      </div>

      <div className="w-80 rounded-xl border p-4">
        <h3 className="font-strong mb-2">Selected Field Types</h3>
        <div className="space-y-2">
          {values.map((value) => {
            const option = options.find((opt) => opt.value === value);
            return (
              <div
                key={value}
                className="text-body-small flex items-center gap-2"
              >
                {option?.icon}
                <span>{option?.label}</span>
                <span className="text-secondary-foreground font-mono">
                  ({value})
                </span>
              </div>
            );
          })}
          {values.length === 0 && (
            <div className="text-secondary-foreground font-mono">
              No field types selected
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

import { Component } from "@choice-ui/multi-select"

export default function App() {
  return (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="font-strong">Disabled Items</label>
        <MultiSelect values={values} onChange={setValues}>
          <MultiSelect.Trigger
            placeholder="Select options..."
            getDisplayValue={getDisplayValue}
            className="w-80"
          />
          <MultiSelect.Content>
            {options.map((option) => (
