import { Component } from "@choice-ui/select"

export default function App() {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "20px",
        alignItems: "flex-start",
      }}
    >
      <label style={{ display: "flex", alignItems: "center", gap: "8px" }}>
        <input
          type="checkbox"
          checked={closeOnEscape}
          onChange={(e) => setCloseOnEscape(e.target.checked)}
        />
        <span>Enable close on ESC key</span>
      </label>

      <Select value={value} onChange={setValue} closeOnEscape={closeOnEscape}>
        <Select.Trigger>
          <Select.Value>{value || "Select an option..."}</Select.Value>
        </Select.Trigger>
        <Select.Content>
          {Array.from({ length: 5 }, (_, i) => (
            <Select.Item key={i} value={`option-${i + 1}`}>
              <Select.Value>Option {i + 1}</Select.Value>
            </Select.Item>
          ))}
        </Select.Content>
      </Select>

      <div style={{ fontSize: "14px", color: "#666", maxWidth: "400px" }}>
        {closeOnEscape
          ? "‚úÖ Try opening the select and pressing ESC - it will close the menu."
          : "‚ùå With this option disabled, pressing ESC will not close the select menu."}
      </div>
    </div>
  );
}

import { Component } from "@choice-ui/select"

export default function App() {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "20px",
        alignItems: "flex-start",
      }}
    >
      <Select value={value} onChange={setValue} closeOnEscape={false}>
        <Select.Trigger>
          <Select.Value>{value || "Select an option..."}</Select.Value>
        </Select.Trigger>
        <Select.Content>
          {Array.from({ length: 5 }, (_, i) => (
            <Select.Item key={i} value={`option-${i + 1}`}>
              <Select.Value>Option {i + 1}</Select.Value>
            </Select.Item>
          ))}
        </Select.Content>
      </Select>

      <div
        style={{
          padding: "12px 16px",
          backgroundColor: "#fff3cd",
          border: "1px solid #ffc107",
          borderRadius: "4px",
          fontSize: "14px",
          maxWidth: "400px",
        }}
      >
        <strong>‚ö†Ô∏è Note:</strong> Pressing ESC key will NOT close this select
        menu. Click outside or select an option to close it.
      </div>
    </div>
  );
}

