import { Table } from "@choice-ui/table"
import { useState, useMemo } from "react"

export default function App() {

  const users = useMemo(() => generateUsers(10), [])
  const [selectedKeys, setSelectedKeys] = useState<(string | number)[]>([])

  return (
    <div className="flex flex-col gap-4">
      <div className="text-secondary-foreground text-sm">
        Selected: {selectedKeys.length > 0 ? selectedKeys[0] : "None"}
      </div>
      <Table
        data={users}
        getRowKey={(user) => user.id}
        selectable
        selectionMode="single"
        selectedKeys={selectedKeys}
        onSelectionChange={setSelectedKeys}
        height={300}
        className="w-full rounded-lg border"
      >
        <Table.Header>
          <Table.Column id="name" width={200}>
            Name
          </Table.Column>
          <Table.Column id="email">Email</Table.Column>
          <Table.Column id="department" width={150}>
            Department
          </Table.Column>
        </Table.Header>
        <Table.Body>
          {(user, index) => (
            <Table.Row
              rowKey={user.id}
              index={index}
            >
              <Table.Cell columnId="name">{user.name}</Table.Cell>
              <Table.Cell columnId="email">{user.email}</Table.Cell>
              <Table.Cell columnId="department">{user.department}</Table.Cell>
            </Table.Row>
          )}
        </Table.Body>
      </Table>
    </div>
  )

}

import { Table } from "@choice-ui/table"
import { useState, useMemo } from "react"

export default function App() {

  const users = useMemo(() => generateUsers(10), [])
  const [clickedUser, setClickedUser] = useState<string | null>(null)

  return (
    <div className="flex flex-col gap-4">
      <div className="text-secondary-foreground text-sm">
        Last clicked: {clickedUser || "None"}
      </div>
      <Table
        data={users}
        getRowKey={(user) => user.id}
        selectable={false}
        onRowClick={(user) => setClickedUser(user.name)}
        height={300}
        className="w-full rounded-lg border"
      >
        <Table.Header>
          <Table.Column id="name" width={200}>
            Name
          </Table.Column>
          <Table.Column id="email">Email</Table.Column>
          <Table.Column id="joinDate" width={120}>
            Join Date
          </Table.Column>
        </Table.Header>
        <Table.Body>
          {(user, index) => (
            <Table.Row
              rowKey={user.id}
              index={index}
            >
              <Table.Cell columnId="name">{user.name}</Table.Cell>
              <Table.Cell columnId="email">{user.email}</Table.Cell>
              <Table.Cell columnId="joinDate">{user.joinDate}</Table.Cell>
            </Table.Row>
          )}
        </Table.Body>
      </Table>
    </div>
  )

}

import { Table } from "@choice-ui/table"
import { useState, useMemo } from "react"

export default function App() {

  const users = useMemo(() => generateUsers(10000), [])
  const [selectedKeys, setSelectedKeys] = useState<(string | number)[]>([])

  return (
    <div className="flex flex-col gap-4">
      <div className="text-secondary-foreground text-sm">
        Total rows: {users.length} | Selected: {selectedKeys.length}
      </div>
      <Table
        data={users}
        getRowKey={(user) => user.id}
        selectable
        virtualized
        estimateRowHeight={32}
        overscan={10}
        selectedKeys={selectedKeys}
        onSelectionChange={setSelectedKeys}
        height={500}
        className="w-full rounded-lg border"
      >
        <Table.Header>
          <Table.Column id="id" width={100}>
            ID
          </Table.Column>
          <Table.Column id="name" width={200}>
            Name
          </Table.Column>
          <Table.Column id="email">Email</Table.Column>
          <Table.Column id="role" width={120}>
            Role
          </Table.Column>
          <Table.Column id="status" width={100}>
            Status
          </Table.Column>
        </Table.Header>
        <Table.Body>
          {(user, index) => (
            <Table.Row
              rowKey={user.id}
              index={index}
            >
              <Table.Cell columnId="id">{user.id}</Table.Cell>
              <Table.Cell columnId="name">{user.name}</Table.Cell>
              <Table.Cell columnId="email">{user.email}</Table.Cell>
              <Table.Cell columnId="role">{user.role}</Table.Cell>
              <Table.Cell columnId="status">
                <StatusBadge status={user.status} />
              </Table.Cell>
            </Table.Row>
          )}
        </Table.Body>
      </Table>
    </div>
  )

}

import { Table } from "@choice-ui/table"
import { useMemo, useRef } from "react"

export default function App() {

  const users = useMemo(() => generateUsers(100), [])
  const scrollRef = useRef<HTMLDivElement>(null)
  const containerRef = useRef<HTMLDivElement>(null)

  return (
    <ScrollArea className="h-[400px] w-full rounded-lg border">
      <ScrollArea.Viewport ref={scrollRef}>
        <ScrollArea.Content ref={containerRef}>
          <Table
            data={users}
            getRowKey={(user) => user.id}
            selectable
            scrollRef={scrollRef}
            containerRef={containerRef}
            virtualized
          >
            <Table.Header>
              <Table.Column id="name" width={200}>
                Name
              </Table.Column>
              <Table.Column id="email">Email</Table.Column>
              <Table.Column id="role" width={120}>
                Role
              </Table.Column>
            </Table.Header>
            <Table.Body>
              {(user, index) => (
                <Table.Row
                  rowKey={user.id}
                  index={index}
                >
                  <Table.Cell columnId="name">{user.name}</Table.Cell>
                  <Table.Cell columnId="email">{user.email}</Table.Cell>
                  <Table.Cell columnId="role">{user.role}</Table.Cell>
                </Table.Row>
              )}
            </Table.Body>
          </Table>
        </ScrollArea.Content>
      </ScrollArea.Viewport>
    </ScrollArea>
  )

}

