import { Table } from "@choice-ui/table"
import { useState, useMemo } from "react"

export default function App() {

  const users = useMemo(() => generateUsers(30), [])
  const [selectedKeys, setSelectedKeys] = useState<(string | number)[]>([])

  return (
    <div className="flex flex-col gap-4">
      <div className="bg-accent-background/10 rounded-md p-4">
        <p className="text-sm">
          <strong>Tip:</strong> Hold <kbd className="bg-secondary-background rounded px-1">Shift</kbd> and
          click checkboxes to select a range of rows.
        </p>
      </div>
      <div className="text-secondary-foreground text-sm">Selected: {selectedKeys.length} rows</div>
      <Table
        data={users}
        getRowKey={(user) => user.id}
        selectable
        selectionMode="multiple"
        selectedKeys={selectedKeys}
        onSelectionChange={setSelectedKeys}
        height={400}
        className="w-full rounded-lg border"
      >
        <Table.Header>
          <Table.Column id="name" width={200}>
            Name
          </Table.Column>
          <Table.Column id="email">Email</Table.Column>
          <Table.Column id="department" width={150}>
            Department
          </Table.Column>
        </Table.Header>
        <Table.Body>
          {(user, index) => (
            <Table.Row
              rowKey={user.id}
              index={index}
            >
              <Table.Cell columnId="name">{user.name}</Table.Cell>
              <Table.Cell columnId="email">{user.email}</Table.Cell>
              <Table.Cell columnId="department">{user.department}</Table.Cell>
            </Table.Row>
          )}
        </Table.Body>
      </Table>
    </div>
  )

}

import { Table } from "@choice-ui/table"
import { useState, useMemo } from "react"

export default function App() {

  const [rowCount, setRowCount] = useState(100)
  const [selectionMode, setSelectionMode] = useState<"none" | "single" | "multiple">("multiple")
  const [virtualized, setVirtualized] = useState(true)
  const [selectedKeys, setSelectedKeys] = useState<(string | number)[]>([])

  const users = useMemo(() => generateUsers(rowCount), [rowCount])

  return (
    <div className="flex flex-col gap-4">
      <div className="flex flex-wrap items-center gap-4">
        <label className="flex items-center gap-2">
          <span className="text-sm">Rows:</span>
          <select
            value={rowCount}
            onChange={(e) => setRowCount(Number(e.target.value))}
            className="rounded border px-2 py-1"
          >
            <option value={10}>10</option>
            <option value={100}>100</option>
            <option value={1000}>1,000</option>
            <option value={10000}>10,000</option>
          </select>
        </label>
        <label className="flex items-center gap-2">
          <span className="text-sm">Selection:</span>
          <select
            value={selectionMode}
            onChange={(e) => setSelectionMode(e.target.value as typeof selectionMode)}
            className="rounded border px-2 py-1"
          >
            <option value="none">None</option>
            <option value="single">Single</option>
            <option value="multiple">Multiple</option>
          </select>
        </label>
        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={virtualized}
            onChange={(e) => setVirtualized(e.target.checked)}
          />
          <span className="text-sm">Virtualized</span>
        </label>
        {selectionMode !== "none" && (
          <span className="text-secondary-foreground text-sm">Selected: {selectedKeys.length}</span>
        )}
      </div>
      <Table
        data={users}
        getRowKey={(user) => user.id}
        selectable={selectionMode !== "none"}
        selectionMode={selectionMode === "none" ? "multiple" : selectionMode}
        virtualized={virtualized}
        selectedKeys={selectedKeys}
        onSelectionChange={setSelectedKeys}
        height={400}
        className="w-full rounded-lg border"
      >
        <Table.Header>
          <Table.Column id="id" width={100}>
            ID
          </Table.Column>
          <Table.Column id="name" width={200}>
            Name
          </Table.Column>
          <Table.Column id="email">Email</Table.Column>
          <Table.Column id="role" width={120}>
            Role
          </Table.Column>
          <Table.Column id="status" width={100}>
            Status
          </Table.Column>
        </Table.Header>
        <Table.Body>
          {(user, index) => (
            <Table.Row
              rowKey={user.id}
              index={index}
            >
              <Table.Cell columnId="id">{user.id}</Table.Cell>
              <Table.Cell columnId="name">{user.name}</Table.Cell>
              <Table.Cell columnId="email">{user.email}</Table.Cell>
              <Table.Cell columnId="role">{user.role}</Table.Cell>
              <Table.Cell columnId="status">
                <StatusBadge status={user.status} />
              </Table.Cell>
            </Table.Row>
          )}
        </Table.Body>
      </Table>
    </div>
  )

}
