import { DateInput } from "@choice-ui/calendar"

export default function App() {
  return (
    <DateInput {...args} />
  );
}

import { DateInput } from "@choice-ui/calendar"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<Date | null>(new Date())
  const [changeCount, setChangeCount] = useState(0)

  const handleChange = (newValue: Date | null) => {
    setValue(newValue)
    setChangeCount((prev) => prev + 1)
  }

  return (
    <div className="flex flex-col gap-4">
      <div className="rounded-xl border bg-stone-50 p-4">
        <div className="text-body-small-strong mb-2 text-stone-700">Current Value:</div>
        <div className="text-body-small font-mono text-stone-600">
          {value ? value.toLocaleDateString() : "null"}
        </div>
        <div className="text-body-small-strong mt-2 text-stone-700">Change Count:</div>
        <div className="text-body-small font-mono text-stone-600">{changeCount}</div>
      </div>
      <DateInput
        readOnly
        value={value}
        onChange={handleChange}
        placeholder="Readonly date input..."
      />
      <DateInput
        value={value}
        onChange={handleChange}
        placeholder="Normal date input..."
      />
      <div className="text-body-small text-stone-600">
        ðŸ’¡ Try changing the readonly date input - the value should not change and the change count
        should remain at 0. Only the normal input will change the value.
      </div>
    </div>
  )

}

