import { MonthCalendar } from "@choice-ui/calendar"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<CalendarValue>(null)

  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <MonthCalendar
          value={value}
          onChange={setValue}
          selectionMode="single"
          highlightToday
          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
        />
        <MonthCalendar
          value={value}
          onChange={setValue}
          selectionMode="single"
          highlightToday
          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
          variant="dark"
        />
      </div>
      {value && value instanceof Date && (
        <p className="text-gray-600">Selected: {value.toLocaleDateString("zh-CN")}</p>
      )}
    </div>
  )

}

import { MonthCalendar } from "@choice-ui/calendar"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<CalendarValue>([])

  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <MonthCalendar
          value={value}
          onChange={setValue}
          selectionMode="multiple"
          highlightToday
          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
        />
        <MonthCalendar
          value={value}
          onChange={setValue}
          selectionMode="multiple"
          highlightToday
          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
          variant="dark"
        />
      </div>
      {Array.isArray(value) && value.length > 0 && (
        <div className="text-secondary-foreground">
          <p>Selected date ({value.length}):</p>
          <ul className="list-inside list-disc">
            {value.map((date, index) => (
              <li key={index}>{date.toLocaleDateString("zh-CN")}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )

}

