import { MonthCalendar } from "@choice-ui/calendar"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<CalendarValue>(null)

  const today = new Date()
  const disabledDates = [
    new Date(today.getTime() - 24 * 60 * 60 * 1000),
    new Date(today.getTime() - 2 * 24 * 60 * 60 * 1000),
    new Date(today.getFullYear(), today.getMonth(), 15),
    new Date(today.getFullYear(), today.getMonth(), 25),
  ]

  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <MonthCalendar
          value={value}
          onChange={setValue}
          disabledDates={disabledDates}
          selectionMode="single"
          highlightToday
          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
        />
        <MonthCalendar
          value={value}
          onChange={setValue}
          disabledDates={disabledDates}
          selectionMode="single"
          highlightToday
          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
          variant="dark"
        />
      </div>
      <div className="text-secondary-foreground">
        <p>
          Disabled dates include: yesterday, the day before yesterday, the 15th and 25th of each
          month
        </p>
        {value && value instanceof Date && (
          <p>Selected date: {value.toLocaleDateString("zh-CN")}</p>
        )}
      </div>
    </div>
  )

}

