          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
        />
        <MonthCalendar
          value={value}
          onChange={setValue}
          highlightDates={highlightDates}
          selectionMode="single"
          highlightToday
          showOutsideDays
          weekStartsOn={1}
          locale="zh-CN"
          fixedGrid
          className="w-50 rounded-xl border"
