props.description:
Range length display precision, control the minimum unit of range calculation
@example 0.5 - Minimum unit is 0.5 days (half a day)
@example 0.1 - Minimum unit is 0.1 days (2.4 hours)
@example 0.25 - Minimum unit is 0.25 days (6 hours)

stories.source:
import { TimeCalendar, createTimeToday } from "@choice-ui/calendar"
import { useState } from "react"

export default function App() {

  const [time, setTime] = useState<Date | null>(createTimeToday(0, 30))

  const formatTime = (t: Date | null) =>
    t ? t.toLocaleTimeString("zh-CN", { hour: "2-digit", minute: "2-digit" }) : "Select time"

  return (
    <div className="space-y-4">
      <TimeCalendar
        value={time}
        onChange={setTime}
        format="HH:mm"
        step={15}
      >
        <TimeCalendar.Trigger>{formatTime(time)}</TimeCalendar.Trigger>
      </TimeCalendar>
      <div className="text-secondary-foreground">Selected time: {formatTime(time)}</div>
    </div>
  )

}

