import { Avatar } from "@choice-ui/avatar"

export default function App() {
  return (
    <div className="grid grid-cols-4 gap-4">
      {colors.map((color) => (
        <div
          key={color.name}
          className="flex flex-col items-center gap-2"
        >
          <Avatar
            name={color.name}
            color={color.color}
          />
          <p>{color.name}</p>
        </div>
      ))}
    </div>
  );
}

import { Avatar } from "@choice-ui/avatar"

export default function App() {
  return (
    <div className="grid grid-cols-4 gap-4">
      {photos.map((photo) => (
        <div
          key={photo.name}
          className="flex flex-col items-center gap-2"
        >
          <Avatar
            name={photo.name}
            photo={photo.photo}
          />
          <p>{photo.name}</p>
        </div>
      ))}
    </div>
  );
}

import { Avatar } from "@choice-ui/avatar"

export default function App() {
  return (
    <div className="space-y-8">
      <div>
        <h3 className="mb-4 text-sm font-medium text-gray-700">Preset Sizes</h3>
        <div className="grid grid-cols-3 gap-4">
          {presetSizes.map((item) => (
            <div
              className="flex flex-col items-center gap-2"
              key={item.label}
            >
              <Avatar
                name={item.label}
                size={item.size}
                color={faker.color.rgb()}
              />
              <p className="text-sm">{item.label}</p>
            </div>
          ))}
        </div>
      </div>

      <div>
        <h3 className="mb-4 text-sm font-medium text-gray-700">Numeric Sizes</h3>
        <div className="grid grid-cols-4 gap-4">
          {numericSizes.map((size) => (
            <div
              className="flex flex-col items-center gap-2"
              key={size}
            >
              <Avatar
                name={`${size}px`}
                size={size}
                color={faker.color.rgb()}
              />
              <p className="text-sm">{size}px</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

import { Avatar } from "@choice-ui/avatar"

export default function App() {
  return (
    <div className="grid grid-cols-4 gap-4">
      {Object.values(States).map((state) => (
        <div
          className="flex flex-col items-center gap-2"
          key={state}
        >
          <Avatar
            name={state}
            states={state}
          />
          <p>{state}</p>
        </div>
      ))}
    </div>
  );
}

import { Avatar } from "@choice-ui/avatar"

export default function App() {
  return (
    <div className="grid grid-cols-5 gap-6">
      {roundedClasses.map((rounded) => (
        <div
          className="flex flex-col items-center gap-2"
          key={rounded.class}
        >
          <Avatar
            name="A"
            className={rounded.class}
            photo="https://api.dicebear.com/7.x/avataaars/svg?seed=1"
            size="large"
          />
          <p className="text-sm">{rounded.label}</p>
        </div>
      ))}
    </div>
  );
}
