import { Component } from "@choice-ui/hint"

export default function App() {
  return (
    <div className="flex items-center gap-2">
      <span className="font-strong">Reason</span>
      <Hint content="Optional reason" placement="right-start" />
    </div>
  );
}

import { Component } from "@choice-ui/hint"

