      value={45}
      showValue
    >
      <ProgressBar.Track>
        <ProgressBar.Connects />
      </ProgressBar.Track>
    </ProgressBar>
  );
}

import { ProgressBar } from "@choice-ui/progress"

export default function App() {
  return (
    <div className="grid gap-4">
      {variants.map((variant) => (
        <Fragment key={variant}>
          <span className="capitalize">{variant}</span>
          <ProgressBar
            variant={variant}
            value={72}
            showValue
          >
            <ProgressBar.Label>Uploading</ProgressBar.Label>
            <ProgressBar.Track>
              <ProgressBar.Connects />
            </ProgressBar.Track>
          </ProgressBar>
        </Fragment>
      ))}
    </div>
  );
}

import { ProgressBar } from "@choice-ui/progress"

export default function App() {
  return (
    <div className="grid gap-4">
      <div className="grid gap-2">
        <span>Evenly spaced colors</span>
        <ProgressBar
          value={value}
          variant="based-on-value"
          dynamicColors={["#ef4444", "#f59e0b", "#22c55e"]}
        >
          <ProgressBar.Label showValue>Score</ProgressBar.Label>
          <ProgressBar.Track>
            <ProgressBar.Connects />
          </ProgressBar.Track>
        </ProgressBar>
      </div>

      <div className="grid gap-2">
        <span>Custom stops (at)</span>
        <ProgressBar
          value={value}
          variant="based-on-value"
          dynamicColors={[
            { at: 0, color: "#ef4444" },
            { at: 0.5, color: "#f59e0b" },
            { at: 1, color: "#22c55e" },
          ]}
        >
          <ProgressBar.Label showValue>Health</ProgressBar.Label>
          <ProgressBar.Track>
            <ProgressBar.Connects />
          </ProgressBar.Track>
        </ProgressBar>
      </div>

      <div className="grid gap-2">
        <Range
          min={0}
          max={100}
          value={value}
          onChange={(v) => setValue(v)}
          trackSize={{
            width: "auto",
            height: 16,
          }}
        />
        <span>Value: {value}</span>
      </div>
    </div>
  );
}
