import {  } from "@choice-ui/progress-bar"

export default function App() {

  const variants: Array<NonNullable<React.ComponentProps<typeof ProgressBar>["variant"]>> = [
    "accent",
    "default",
  ]

  return (
    <div className="grid w-80 gap-4">
      {variants.map((variant) => (
        <Fragment key={variant}>
          <span className="capitalize">{variant}</span>
          <ProgressBar
            variant={variant}
            value={72}
            showValue
          >
            <ProgressBar.Label>Uploading</ProgressBar.Label>
            <ProgressBar.Track>
              <ProgressBar.Connects />
            </ProgressBar.Track>
          </ProgressBar>
        </Fragment>
      ))}
    </div>
  )

}

import {  } from "@choice-ui/progress-bar"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState(35)

  return (
    <div className="grid w-80 gap-4">
      <div className="grid gap-2">
        <span>Evenly spaced colors</span>
        <ProgressBar
          value={value}
          variant="based-on-value"
          dynamicColors={["#ef4444", "#f59e0b", "#22c55e"]}
        >
          <ProgressBar.Label showValue>Score</ProgressBar.Label>
          <ProgressBar.Track>
            <ProgressBar.Connects />
          </ProgressBar.Track>
        </ProgressBar>
      </div>

      <div className="grid gap-2">
        <span>Custom stops (at)</span>
        <ProgressBar
          value={value}
          variant="based-on-value"
          dynamicColors={[
            { at: 0, color: "#ff0000" },
            { at: 0.5, color: "#ffa500" },
            { at: 1, color: "#ffff00" },
          ]}
        >
          <ProgressBar.Label showValue>Health</ProgressBar.Label>
          <ProgressBar.Track>
            <ProgressBar.Connects />
          </ProgressBar.Track>
        </ProgressBar>
      </div>

      <div className="grid gap-2">
        <Range
          min={0}
          max={100}
          value={value}
          onChange={(v) => setValue(v)}
          trackSize={{
            width: "auto",
            height: 16,
          }}
        />
        <span>Value: {value}</span>
      </div>
    </div>
  )

}
