import { Component } from "@choice-ui/progress"

export default function App() {
  return (
    <ProgressCircle value={72}>
      <ProgressCircle.Value />
    </ProgressCircle>
  );
}

import { Component } from "@choice-ui/progress"

export default function App() {
  return (
    <div className="grid gap-4">
      {variants.map((variant) => (
        <Fragment key={variant}>
          <span className="capitalize">{variant}</span>
          <div className="flex items-center gap-4">
            <ProgressCircle variant={variant} value={40} />
            <ProgressCircle variant={variant} value={70}>
              <ProgressCircle.Value />
            </ProgressCircle>
          </div>
        </Fragment>
      ))}
    </div>
  );
}

import { Component } from "@choice-ui/progress"

export default function App() {
  return (
    <div className="grid grid-cols-2 gap-4">
      <div className="grid place-items-center gap-2">
        <span>Evenly spaced colors</span>
        <ProgressCircle
          value={value}
          variant="based-on-value"
          dynamicColors={["#ef4444", "#f59e0b", "#22c55e"]}
        >
          <ProgressCircle.Value />
        </ProgressCircle>
      </div>

      <div className="grid place-items-center gap-2">
        <span>Custom stops (at)</span>
        <ProgressCircle
          value={value}
          variant="based-on-value"
          dynamicColors={[
            { at: 0, color: "#ef4444" },
            { at: 0.5, color: "#f59e0b" },
            { at: 1, color: "#22c55e" },
          ]}
        >
          <ProgressCircle.Value />
        </ProgressCircle>
      </div>

      <div className="col-span-2 grid w-full">
        <Range
          min={0}
          max={100}
          value={value}
          onChange={(v) => setValue(v)}
          trackSize={{ width: "auto", height: 16 }}
        />
        <span>Value: {value}</span>
      </div>
    </div>
  );
}
