import {  } from "@choice-ui/progress-circle"

export default function App() {

  const variants: Array<NonNullable<React.ComponentProps<typeof ProgressCircle>["variant"]>> = [
    "accent",
    "default",
  ]

  return (
    <div className="grid gap-4">
      {variants.map((variant) => (
        <Fragment key={variant}>
          <span className="capitalize">{variant}</span>
          <div className="flex items-center gap-4">
            <ProgressCircle
              variant={variant}
              value={40}
            />
            <ProgressCircle
              variant={variant}
              value={70}
            >
              <ProgressCircle.Value />
            </ProgressCircle>
          </div>
        </Fragment>
      ))}
    </div>
  )

}

import {  } from "@choice-ui/progress-circle"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState(35)

  return (
    <div className="grid grid-cols-2 gap-4">
      <div className="grid place-items-center gap-2">
        <span>Evenly spaced colors</span>
        <ProgressCircle
          value={value}
          variant="based-on-value"
          dynamicColors={["#ef4444", "#f59e0b", "#22c55e"]}
        >
          <ProgressCircle.Value />
        </ProgressCircle>
      </div>

      <div className="grid place-items-center gap-2">
        <span>Custom stops (at)</span>
        <ProgressCircle
          value={value}
          variant="based-on-value"
          dynamicColors={[
            { at: 0, color: "#ff0000" },
            { at: 0.5, color: "#ffa500" },
            { at: 1, color: "#ffff00" },
          ]}
        >
          <ProgressCircle.Value />
        </ProgressCircle>
      </div>

      <div className="col-span-2 grid w-full gap-2">
        <Range
          min={0}
          max={100}
          value={value}
          onChange={(v) => setValue(v)}
          trackSize={{ width: "auto", height: 16 }}
        />
        <span>Value: {value}</span>
      </div>
    </div>
  )

}
