import { Checkbox } from "@choice-ui/checkbox"

export default function App() {
  return (
    <div className="flex flex-col items-start gap-4">
      {Object.values(Variant).map((variant, index) => (
        <Fragment key={variant}>
          <span className="capitalize">{variant}</span>
          <div className="grid grid-cols-4 gap-2">
            {Object.values(State).map((state) => (
              <Fragment key={state}>
                <span className="text-pink-500 capitalize">{state}</span>

                {Object.values(Interaction).map((interaction) => (
                  <Fragment key={interaction}>
                    <Checkbox
                      value={
                        interaction === Interaction.On ||
                        interaction === Interaction.Mixed
                      }
                      mixed={interaction === Interaction.Mixed}
                      disabled={state === State.Disabled}
                      focused={state === State.Focused}
                      variant={variant}
                    >
                      {interaction}
                    </Checkbox>
                  </Fragment>
                ))}
              </Fragment>
            ))}
          </div>
          {index !== Object.values(Variant).length - 1 && (
            <hr className="w-full" />
          )}
        </Fragment>
      ))}
    </div>
  );
}

import { Checkbox } from "@choice-ui/checkbox"

export default function App() {
  return (
    <Checkbox value={value} onChange={(value) => setValue(value)} disabled>
      Disabled
    </Checkbox>
  );
}

import { Checkbox } from "@choice-ui/checkbox"

export default function App() {
  return (
    <div className="flex flex-col gap-2">
      <Checkbox
        value={variant.default}
        onChange={(value) => setVariant({ ...variant, default: value })}
      >
        Default
      </Checkbox>
      <Checkbox
        value={variant.accent}
        onChange={(value) => setVariant({ ...variant, accent: value })}
        variant="accent"
      >
        Accent
      </Checkbox>
      <Checkbox
        value={variant.outline}
        onChange={(value) => setVariant({ ...variant, outline: value })}
        variant="outline"
      >
        Outline
      </Checkbox>
    </div>
  );
}

import { Checkbox } from "@choice-ui/checkbox"

export default function App() {
  return (
    <div className="flex flex-col gap-2">
      {groupOptions.map((option) => (
        <Checkbox
          key={option.id}
          value={selectedIds.includes(option.id)}
          onChange={(checked) => {
            setSelectedIds((prev) =>
              checked
                ? [...prev, option.id]
                : prev.filter((id) => id !== option.id)
            );
          }}
        >
          {option.label}
        </Checkbox>
      ))}
    </div>
  );
}
