import { ChipsInput } from "@choice-ui/chips-input"

export default function App() {

  const customRenderChip = ({
    chip,
    index,
    isSelected,
    disabled,
    handleChipClick,
    handleChipRemoveClick,
  }: RenderChipProps) => {
    return (
      <div
        style={{
          padding: "2px 8px",
          margin: "2px",
          border: isSelected ? "2px solid #007bff" : "1px solid #ced4da",
          borderRadius: "12px",
          display: "inline-flex",
          alignItems: "center",
          opacity: disabled ? 0.5 : 1,
          cursor: disabled ? "not-allowed" : "pointer",
          backgroundColor: isSelected ? "#cfe2ff" : "#f8f9fa",
          fontSize: "0.875rem",
          transition: "all 0.2s ease-in-out",
        }}
        onClick={disabled ? undefined : () => handleChipClick(index)}
      >
        <span style={{ marginRight: "4px" }}>
          {isSelected ? "üåü" : "üè∑Ô∏è"} {chip}
        </span>
        {!disabled && (
          <button
            onClick={(e) => {
              e.stopPropagation() // Important!
              handleChipRemoveClick(index)
            }}
            style={{
              marginLeft: "4px",
              border: "none",
              background: "none",
              cursor: "pointer",
              padding: 0,
              lineHeight: 1,
            }}
            aria-label={`Remove ${chip}`}
            disabled={disabled}
          >
            <RemoveSmall />
          </button>
        )}
      </div>
    )
  }

  return (
    <ChipsInput
      placeholder="Add custom chips..."
      renderChip={customRenderChip}
      className="w-64"
    />
  )

}
