import { CheckboxAdapter } from "@choice-ui/form"

export default function App() {
  return (
    <>
      <form
        className="w-80 space-y-4"
        onSubmit={(e) => {
          e.preventDefault()
          e.stopPropagation()
          form.handleSubmit()
        }}
      >
        <form.Field
          name={`username-${uuid}`}
          validators={{
            onChange: ({ value }) => {
              if ((value as string).length < 3) {
                return "Username must be at least 3 characters"
              }
            },
          }}
        >
          {(field) => (
            <form.Input
              name={field.name}
              label={<div className="text-red-500">onChange validation</div>}
              value={field.state.value as string}
              onChange={field.handleChange}
              onBlur={field.handleBlur}
              placeholder="Enter username"
              error={<em role="alert">{field.state.meta.errors.join(", ")}</em>}
            />
          )}
        </form.Field>

        <form.Field
          name={`email-${uuid}`}
          validators={{
            onBlur: ({ value }) => {
              if ((value as string).length < 3) {
                return "Email must be at least 3 characters"
              }
            },
          }}
        >
          {(field) => (
            <form.Input
              name={field.name}
              label="onBlur validation"
              value={field.state.value as string}
              onChange={field.handleChange}
              onBlur={field.handleBlur}
              placeholder="Enter email"
              error={<em role="alert">{field.state.meta.errors.join(", ")}</em>}
            />
          )}
        </form.Field>

        <form.Button type="submit">Submit Form</form.Button>
      </form>

      <div className="bg-secondary-background mt-4 rounded-xl p-4">
        <strong>Form Result:</strong>
        <CodeBlock language="json">
          <CodeBlock.Content code={result} />
        </CodeBlock>
      </div>
    </>
  );
}

