import { CheckboxAdapter } from "@choice-ui/form"

export default function App() {
  return (
    <>
      <form
        className="w-80 space-y-4"
        onSubmit={(e) => {
          e.preventDefault();
          e.stopPropagation();
          form.handleSubmit();
        }}
      >
        <form.Field
          name="password"
          validators={{
            onChange: ({ value }) => {
              if ((value as string).length < 6) {
                return "Password must be at least 6 characters";
              }
            },
          }}
        >
          {(field) => (
            <form.Input
              label="Password"
              name={field.name}
              value={field.state.value as string}
              onChange={field.handleChange}
              onBlur={field.handleBlur}
              type="password"
              placeholder="Enter password"
              error={field.state.meta.errors.join(", ")}
            />
          )}
        </form.Field>

        <form.Field
          name="confirmPassword"
          validators={{
            onChange: ({ value }) => {
              if (value !== form.state.values.password) {
                return "Passwords do not match";
              }
            },
          }}
        >
          {(field) => (
            <form.Input
              label="Confirm Password"
              name={field.name}
              value={field.state.value as string}
              onChange={field.handleChange}
              onBlur={field.handleBlur}
              type="password"
              placeholder="Confirm password"
              error={field.state.meta.errors.join(", ")}
            />
          )}
        </form.Field>

        <form.Field
          name="age"
          validators={{
            onChange: ({ value }) => {
              const age = parseInt(value as string);
              if (isNaN(age) || age < 1) {
                return "Please enter a valid age";
              }
              if (age < 18 || age > 80) {
                return "Must be 18 or older and less than 80";
              }
            },
          }}
        >
          {(field) => (
            <form.Input
              label="Age"
              name={field.name}
              value={field.state.value as string}
              onChange={field.handleChange}
              onBlur={field.handleBlur}
              type="number"
              placeholder="Enter age"
              error={field.state.meta.errors.join(", ")}
            />
          )}
        </form.Field>

        <form.Button type="submit">Create Account</form.Button>
      </form>

      <div className="bg-secondary-background mt-4 rounded-xl p-4">
        <strong>Form Result:</strong>
        <CodeBlock language="json">
          <CodeBlock.Content code={result} />
        </CodeBlock>
      </div>
    </>
  );
}

