import { Input } from "@choice-ui/input"

export default function App() {
  return (
    <Input className="w-64" />
  );
}

import { Input } from "@choice-ui/input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState("Disabled")
  return (
    <Input
      className="w-64"
      value={value}
      onChange={(value) => setValue(value)}
      disabled
    />
  )

}

